public void linkActive()
{
    ledgerJournalEngine.newJournalActive(ledgerJournalTable);
    ledgerJournalEngine.formMethodDataSourceLinkActivePre();
    journalFormTrans.datasourceLinkActivePre();

    super();

    if (ledgerJournalTable.JournalNum != oldLedgerJournalId)
    {
        element.initLedger();

        // datasourceActivePost show and hides fields and button and is normally run by the Active method.
        // If no lines exist the Active method does not run but we still need the method called.
        if (ledgerJournalTable.numOfLines() == 0)
        {
            journalFormTrans.datasourceActivePost();
        }

        oldLedgerJournalId = ledgerJournalTable.JournalNum;
    }

    // when entering with a newly created ledgerJournalTable record the allow create is set to true.
    // specify as false to handle both cases of new and existing when LedgerJournalTransEliminationEditableFlight is not enabled.
    ledgerJournalTrans_ds.allowDelete(isLineEditable);
    ledgerJournalTrans_ds.allowCreate(isLineEditable);
}

public void init()
{
    super();

    QueryBuildRange ledgerJournalTransRange   = this.query().dataSourceNo(1).addRange(fieldNum(LedgerJournalTrans, Invisible));
    ledgerJournalTransRange.value(strFmt('%1',0));

    ledgerJournalEngine = new LedgerJournalEngine_Elimination(element);
    ledgerJournalEngine.ledgerJournalTable(element.args().record());

    journalFormTrans.datasourceInitPost(ledgerJournalTrans);
    this.cacheAddMethod(tableMethodStr(LedgerJournalTrans, accountName));
}

public void delete()
{
    LedgerJournalTrans ledgerJournalTrans_Orig;

    ledgerJournalEngine.rereadWhenDirty(ledgerJournalTrans);
    ledgerJournalTrans_Orig = ledgerJournalTrans.data();

    ledgerJournalEngine.preDelete(ledgerJournalTrans);
    ledgerJournalEngine.formMethodDataSourceDeletePre();

    super();

    ledgerJournalEngine.delete(ledgerJournalTrans_Orig);
}

/// <summary>
/// Standard event handler that is called each time a record is read by the data source.
/// </summary>
/// <returns>
/// An integer return code.
/// </returns>
public int active()
{
    int ret;

    ret = super();

    if (ret)
    {
        ledgerJournalEngine.active(ledgerJournalTrans);

        journalFormTrans.datasourceActivePost();

        // do not allow deletion or creation of individual elimination distribution lines when LedgerJournalTransEliminationEditableFlight is not enabled.
        ledgerJournalTrans_ds.allowDelete(isLineEditable);
        ledgerJournalTrans_ds.allowCreate(isLineEditable);
    }

    LedgerJournalTrans_LedgerDimension.parmDataAreaId(ledgerJournalTrans.Company);
    LedgerJournalTrans_LedgerDimension1.parmDataAreaId(ledgerJournalTrans.Company);

    return ret;
}

public display ImageRes errorExist(LedgerJournalTrans _ledgerJournalTrans)
{
    ImageRes res = -1;

    #resAppl;

    if (journalFormTrans.parmCheckPostResults().errorExists(_ledgerJournalTrans.Voucher))
    {
        res = imageListAppl.image(#ImageError);
    }

    return res;
}

