{
    amount.visible(TaxParameters::checkTaxParameters_IN());
}

void updateDesign(InventDimFormDesignUpdate mode)
{
    switch (mode)
    {
        case InventDimFormDesignUpdate::Init:
            if (!inventDimFormSetup)
            {
                inventDimFormSetup = InventDimCtrl_Frm_InvoiceRegister::newFromForm(element);
            }
            break;
        case InventDimFormDesignUpdate::Active,
            InventDimFormDesignUpdate::FieldChange:

            if (inventDimFormSetup)
            {
                inventDimFormSetup.formActiveSetup(InventDimGroupSetup::newItemId(vendInvoiceInfo.ItemId));
                inventDimFormSetup.formSetControls(true);
            }
            break;
        //this is read only form. nothing can be changed on it so other modes (Active, FieldChange) are impossible here.
        default : throw error(strFmt("@SYS54195",funcName()));
    }
}

