public void bookRefresh(boolean _isClosing = false)
{
    LedgerIJournalFormRefreshable callingForm = element.args().caller() as LedgerIJournalFormRefreshable;

    if (callingForm != null)
    {
        callingForm.refresh();
    }

    if (!_isClosing)
    {
        vendLedgerJournalTrans_ds.reread();
    }
}

public boolean canClose()
{
    boolean canClose;

    try
    {
        // Signal that the close event is beginning.
        custLedgerJournalTransEntityManager.getFormDataEventManager().beginFormCloseEvent();
        vendLedgerJournalTransEntityManager.getFormDataEventManager().beginFormCloseEvent();

        canClose = super();

        // Signal that the close event is finished.
        custLedgerJournalTransEntityManager.getFormDataEventManager().endFormCloseEvent();
        vendLedgerJournalTransEntityManager.getFormDataEventManager().endFormCloseEvent();
    }
    catch (Exception::UpdateConflict)
    {
        // This exception should not be handled by the generic handling logic below.

        // Signal that the close event is finished.
        custLedgerJournalTransEntityManager.getFormDataEventManager().endFormCloseEvent();
        vendLedgerJournalTransEntityManager.getFormDataEventManager().endFormCloseEvent();

        // Chain the exception forward.
        throw Exception::UpdateConflict;
    }
    catch
    {
        // Signal that the close event is finished.
        custLedgerJournalTransEntityManager.getFormDataEventManager().endFormCloseEvent();
        vendLedgerJournalTransEntityManager.getFormDataEventManager().endFormCloseEvent();

        // Chain the exception forward.
        throw Exception::Error;
    }

    return canClose;
}

public void close()
{
    ledgerJournalEngine.close();
    ledgerJournalEngine.formMethodClosePre();
    journalFormTrans.formClosePre();
    element.bookRefresh(true);

    super();
}

