/// <summary>
/// Restricts the transaction amount of the netting payment to be less than or equal to the maximum netting amount.
/// </summary>
/// <param name = "_vendLedgerJournalTrans">The vendor journal line that was updated.</param>
/// <param name = "_custVendNetJournalLineLink">The <c>CustVendNetJournalLineLink</c> record for the given journal line.</param>
public void amountModified(LedgerJournalTrans _vendLedgerJournalTrans, CustVendNetJournalLineLink _custVendNetJournalLineLink)
{
    if (abs(_custVendNetJournalLineLink.NetAmount) > abs(_custVendNetJournalLineLink.MaxNetAmount)
        || sign(_custVendNetJournalLineLink.NetAmount) != sign(_custVendNetJournalLineLink.MaxNetAmount))
    {
        _custVendNetJournalLineLink.NetAmount = _custVendNetJournalLineLink.MaxNetAmount;
        info("@CashManagement:NettingJournalManualUpdateExceedsMaxAmount");
    }

    _vendLedgerJournalTrans.amountCur2DebCred(_custVendNetJournalLineLink.NetAmount);
}

/// <summary>
/// Restricts the transaction amount of the netting payment to be less than or equal to the maximum netting amount.
/// </summary>
/// <param name = "_vendLedgerJournalTrans">The journal line that was updated.</param>
/// <param name = "_custVendNetJournalLineLink">The <c>CustVendNetJournalLineLink</c> record for the given journal line.</param>
/// <param name = "_amountMarkedForSettlement">The amount marked for settlement for the given journal line.</param>
public void updateAmountAfterSettleTransactions(
    LedgerJournalTrans _vendledgerJournalTrans,
    CustVendNetJournalLineLink _custVendNetJournalLineLink,
    AmountCur _amountMarkedForSettlement)
{
    AmountCur newNetAmount = _amountMarkedForSettlement;

    if (abs(newNetAmount) > abs(_custVendNetJournalLineLink.MaxNetAmount)
        || sign(newNetAmount) != sign(_custVendNetJournalLineLink.MaxNetAmount))
    {
        newNetAmount = _custVendNetJournalLineLink.MaxNetAmount;
        info("@CashManagement:NettingJournalMarkedTransExceedsMaxAmount");
    }

    _custVendNetJournalLineLink.NetAmount = newNetAmount;
    _vendledgerJournalTrans.amountCur2DebCred(newNetAmount);
}

public boolean leaveRecord(boolean _forceUpdate = false)
{
    boolean ret;

    ret = super(_forceUpdate);

    if (vendLedgerJournalTransEntityManager.getFormDataEventManager().isSiblingWriteRequested())
    {
        // A write was requested on the sibling and the client did not write the entity. This happens
        // when focus is removed from a sibling data source (such as LedgerJournalTrans_Asset) as the
        // client only calls write on that specific sibling and not on the entire entity.

        // Write the entire entity.
        vendLedgerJournalTrans_ds.write();
    }

    return ret;
}

