        throw Exception::Error;
    }
}

/// <summary>
/// Handles the event that is fired each time a record is read by the data source.
/// </summary>
/// <returns>
/// An integer return code.
/// </returns>
public int active()
{
    int ret;

    ret = super();

    if (ret)
    {
        ledgerJournalEngine.active(vendLedgerJournalTrans);

        journalFormTrans.datasourceActivePost();
    }

    journalFormTrans.setDelTransButtonEnabledForJournalLine(vendLedgerJournalTrans.PaymentStatus);

    vendLedgerJournalTrans_OffsetAccount.parmDataAreaId(vendLedgerJournalTrans.getOffsetCompany());
    vendLedgerJournalTrans_OffsetAccountTab.parmDataAreaId(vendLedgerJournalTrans.getOffsetCompany());

    vendLedgerJournalTrans_OffsetAccount.parmCurrency(vendLedgerJournalTrans.CurrencyCode);
    vendLedgerJournalTrans_OffsetAccountTab.parmCurrency(vendLedgerJournalTrans.CurrencyCode);

    vendLedgerJournalTrans_OffsetAccount.parmControlDate(vendLedgerJournalTrans.TransDate);
    vendLedgerJournalTrans_OffsetAccountTab.parmControlDate(vendLedgerJournalTrans.TransDate);

    return ret;
}

