    {
        // Handle any exception and then chain another generic one forward to force the form event to fail. Also, in-memory roll-backs
        // of the transaction buffers must be handled as the kernel does not perform this automatically.
        custLedgerJournalTransEntityManager.getInMemoryRollbackManager().performRollbackForAbortedDelete();

        // Signal that the entity delete event is finished with a failure by calling endEntityDeleteEvent
        // and passing in 'true' for the failure parameter.
        custLedgerJournalTransEntityManager.getFormDataEventManager().endEntityDeleteEvent(true);

        throw Exception::Error;
    }
}

public void create(boolean append = false)
{
    try
    {
        custLedgerJournalTransEntityManager.getFormDataEventManager().beginEntityCreateEvent();

        journalFormTrans.datasourceCreatePre();

        super(append);

        // Signal that the create event is finished.
        custLedgerJournalTransEntityManager.getFormDataEventManager().endEntityCreateEvent();
    }
    catch (Exception::UpdateConflict)
    {
        // This exception should not be handled by the generic handling logic below.

        // Signal that the create event is finished.
        custLedgerJournalTransEntityManager.getFormDataEventManager().endEntityCreateEvent();

        // Chain the exception forward.
        throw Exception::UpdateConflict;
    }
    catch
    {
        // Signal that the create event is finished.
        custLedgerJournalTransEntityManager.getFormDataEventManager().endEntityCreateEvent();

        throw Exception::Error;
    }
}

public void jumpRef()
{
    switch (vendLedgerJournalTrans.AccountType)
    {
        case LedgerJournalACType::Cust:
            CustTable::jumpRefCustomer(vendLedgerJournalTrans.parmAccount(), vendLedgerJournalTrans.Company);
            break;

        case LedgerJournalACType::Vend:
            VendTable::jumpRefVendor(vendLedgerJournalTrans.parmAccount(), vendLedgerJournalTrans.Company);
            break;

        default:
            vendLedgerJournalTrans_AccountNum.jumpRef();
            break;
    }
}

public void init()
{
    super();

    QueryBuildDataSource vendNameLedgerDimensionViewDS = vendLedgerJournalTrans_DS.query().dataSourceTable(tableNum(VendNameLedgerDimensionView));
    vendNameLedgerDimensionViewDS.addLink(fieldNum(LedgerJournalTrans, LedgerDimension), fieldNum(VendNameLedgerDimensionView, ValueCombinationRecId));
    vendNameLedgerDimensionViewDS.addLink(fieldNum(LedgerJournalTrans, Company), fieldNum(VendNameLedgerDimensionView, DataArea));
}

public void reread()
{
    VendNameLedgerDimensionView vendNameLedgerDimensionViewLoc;

    select firstonly vendNameLedgerDimensionViewLoc
        where vendNameLedgerDimensionViewLoc.ValueCombinationRecId == vendLedgerJournalTrans.LedgerDimension
            && vendNameLedgerDimensionViewLoc.DataArea == vendLedgerJournalTrans.Company;

    vendNameLedgerDimensionView.data(vendNameLedgerDimensionViewLoc);
    vendNameLedgerDimensionView_ds.setCurrent();
}

public void clicked()
{
    ledgerJournalEngine.clearVoucher();

    try
    {
        super();
    }
    finally
    {
        journalFormTrans.parmCheckPostResults(ledgerJournalEngine.getPostingResults());

        if (journalFormTrans.parmCheckPostResults().errorInJournal())
        {
            errorExist.visible(true);
        }

        ledgerJournalEngine.refreshWhenPosted();
        journalFormTrans.enableButtonsActive();
        element.bookRefresh();
    }
}

public void clicked()
{
    ledgerJournalEngine.clearVoucher();

    super();

    ledgerJournalEngine.newJournalActive(ledgerJournalTable, true);
    vendLedgerJournalTrans_ds.research();
    journalFormTrans.enableButtonsActive();
}

public void clicked()
{
    LedgerJournalTrans localLedgerJournalTrans;

    localLedgerJournalTrans.data(vendledgerJournalTrans);

    super();

    ledgerJournalEngine.settleTrans(custledgerJournalTrans);

    custledgerJournalTrans_ds.write();

    vendLedgerJournalTrans_ds.updateAmountAfterSettleTransactions(
        vendLedgerJournalTrans,
        custVendNetJournalLineLink,
        -custLedgerJournalTrans.amount());

    vendledgerJournalTrans_ds.write();

    // Recalculate balances
    ledgerJournalEngine.newJournalActive(ledgerJournalTable, true);
    element.args().lookupRecord(localLedgerJournalTrans);
    vendledgerJournalTrans_ds.research();

    overviewGrid.setFocus();
}

public void clicked()
{
    LedgerJournalTrans localLedgerJournalTrans;

    localLedgerJournalTrans.data(vendledgerJournalTrans);

    super();

    ledgerJournalEngine.settleTrans(vendledgerJournalTrans);

    vendLedgerJournalTrans_ds.updateAmountAfterSettleTransactions(
        vendLedgerJournalTrans,
        custVendNetJournalLineLink,
        vendLedgerJournalTrans.amount());

    vendledgerJournalTrans_ds.write();

    // Recalculate balances
    ledgerJournalEngine.newJournalActive(ledgerJournalTable, true);
    element.args().lookupRecord(localLedgerJournalTrans);
    vendledgerJournalTrans_ds.research();

    overviewGrid.setFocus();
}

