        {
            ElectronicPaymentReference.allowEdit(true);
        }
    }
}

/// <summary>
/// Checks whether selected payment journal payment mode is electronic or not.
/// </summary>
/// <returns>
/// returns true if payment mode is electronic else false.
/// </returns>
public boolean isElectronicPayment()
{
    VendPaymModeTable vendPaymModeTable;

    select firstonly RecId from vendPaymModeTable
            where ledgerJournalTrans.PaymMode == vendPaymModeTable.PaymMode
            && vendPaymModeTable.PaymentType == PaymentType::ElectronicPayment;

    return (vendPaymModeTable.RecId != 0);
}

