public int active()
{
    element.updateTaxGroupAllowEditInTaxIntegration();

    int ret;

    ret = super();

    if (ret)
    {
        ledgerJournalEngine.active(ledgerJournalTrans);
        priorAccountType = ledgerJournalTrans.AccountType;
        priorOffsetAccountType = ledgerJournalTrans.OffsetAccountType;
        journalFormTrans.datasourceActivePost();
        element.setProjAccountNum();
    }

    // call to the method to set the Invoice Release Date and Release Comment according to the Approved checkbox
    element.setStoppedReleaseFields();
    // <GEECZ>
    element.initIntracomVATDueDate();
    // </GEECZ>

    element.setVisible();

    if (isConfigurationkeyEnabled(configurationKeyNum(PublicSector)))
    {
        element.setAllowEditTax1099();
        element.setAllowEdit1099GFields();
        element.setAllowEdit1099SFields();
    }

    LedgerJournalTrans_AccountNum.parmDataAreaId(ledgerJournalTrans.Company);
    LedgerJournalTrans_AccountNum1.parmDataAreaId(ledgerJournalTrans.Company);
    Group1_AccountNum.parmDataAreaId(ledgerJournalTrans.Company);

    LedgerJournalTrans_OffsetAccount.parmDataAreaId(ledgerJournalTrans.getOffsetCompany());
    OffsetAccount.parmDataAreaId(ledgerJournalTrans.getOffsetCompany());
    Group1_OffsetAccount.parmDataAreaId(ledgerJournalTrans.getOffsetCompany());

    LedgerJournalTrans_AccountNum.parmCurrency(ledgerJournalTrans.CurrencyCode);
    LedgerJournalTrans_AccountNum1.parmCurrency(ledgerJournalTrans.CurrencyCode);
    Group1_AccountNum.parmCurrency(ledgerJournalTrans.CurrencyCode);

    LedgerJournalTrans_AccountNum.parmControlDate(ledgerJournalTrans.TransDate);
    LedgerJournalTrans_AccountNum1.parmControlDate(ledgerJournalTrans.TransDate);
    Group1_AccountNum.parmControlDate(ledgerJournalTrans.TransDate);

    LedgerJournalTrans_OffsetAccount.parmCurrency(ledgerJournalTrans.CurrencyCode);
    OffsetAccount.parmCurrency(ledgerJournalTrans.CurrencyCode);
    Group1_OffsetAccount.parmCurrency(ledgerJournalTrans.CurrencyCode);

    LedgerJournalTrans_OffsetAccount.parmControlDate(ledgerJournalTrans.TransDate);
    OffsetAccount.parmControlDate(ledgerJournalTrans.TransDate);
    Group1_OffsetAccount.parmControlDate(ledgerJournalTrans.TransDate);

    // Lock the main account segment if "Fixed offset account" is selected in Journal Names
    if (ledgerJournalTrans.OffsetAccountType == LedgerJournalACType::Ledger)
    {
        LedgerJournalTrans_OffsetAccount.parmLockMainAccountSegment(ledgerJournalTable.FixedOffsetAccount);
        OffsetAccount.parmLockMainAccountSegment(ledgerJournalTable.FixedOffsetAccount);
        Group1_OffsetAccount.parmLockMainAccountSegment(ledgerJournalTable.FixedOffsetAccount);
    }

    if (LedgerJournalTrans.RecId != 0)
    {
        keepSalesPriceOnWrite = true;
    }

    return ret;
}

