public boolean validate()
{
    boolean  ret;
    // <GCN>
    RefRecId numberSequenceTableRecId;
    // </GCN>

    ret = super();

    // <GCN>
    if (ret
        && LedgerParameters::isChineseVoucher_CN())
    {
        if (!ledgerJournalTrans.LedgerVoucherType_CN)
        {
            // "Voucher type must be selected first."
            return checkFailed("@GLS60256");
        }
        numberSequenceTableRecId = LedgerVoucherType_CN::find(ledgerJournalTrans.LedgerVoucherType_CN).NumberSequenceTable;

        if (!NumberSeq::numCheckFormat(ledgerJournalTrans.Voucher_CN, NumberSequenceTable::find(numberSequenceTableRecId)))
        {
            return false;
        }
    }
    // </GCN>
    return ret;
}

