/// <summary>
/// Handles the event that fires when focus is given to the form.
/// </summary>
/// <param name="_active">
/// true if active; otherwise, false.
/// </param>
public void activate(boolean _active)
{
    super(_active);

    journalFormTrans.manageEditingModeForApprovedWFApproval();
}

void bookRefresh()
{
    FormRun     callerForm = element.args().caller();

    if (callerForm)
    {
        callerForm.bookRefresh();
    }

    ledgerJournalTrans_ds.reread();
}

void callerRefresh()
{
    ledgerJournalEngine.clearCorrectedTaxAmount(ledgerJournalTrans);
    ledgerJournalTrans_ds.refresh();
}

public boolean canClose()
{
    boolean canClose;

    try
    {
        // Signal that the close event is beginning.
        ledgerJournalTransEntityManager.getFormDataEventManager().beginFormCloseEvent();

        canClose = super();

        // Signal that the close event is finished.
        ledgerJournalTransEntityManager.getFormDataEventManager().endFormCloseEvent();
    }
    catch (Exception::UpdateConflict)
    {
        // This exception should not be handled by the generic handling logic below.

        // Signal that the close event is finished.
        ledgerJournalTransEntityManager.getFormDataEventManager().endFormCloseEvent();

        // Chain the exception forward.
        throw Exception::UpdateConflict;
    }
    catch
    {
        // Signal that the close event is finished.
        ledgerJournalTransEntityManager.getFormDataEventManager().endFormCloseEvent();

        // Chain the exception forward.
        throw Exception::Error;
    }

    return canClose;
}

void close()
{
    this.updateJournalBalance();

    ledgerJournalEngine.close();
    ledgerJournalEngine.formMethodClosePre();
    journalFormTrans.formClosePre();
    element.bookRefresh();

    super();
}

/// <summary>
///    Enable or disble approval buttons. 
/// </summary>
/// <param name = "_approved"></param>
/// <param name = "_confirmed"></param>
/// <param name = "_canceled"></param>
/// <param name = "_rejected"></param>
private void setApprovalButtons(boolean _approved,
                            boolean _confirmed,
                            boolean _canceled,
                            boolean _rejected)
{
    rCashTransApprove.enabled(_approved);
    rCashTransConfirm.enabled(_confirmed);
    rCashTransCancel.enabled(_canceled);
    rCashTransReject.enabled(_rejected);
}

void enableButtonsActive()
{
    boolean             posted;
    boolean             confirmActive;
    RCashTransStatus    markedStatus = ledgerJournalEngine.minMarkedCashStatus();

    posted        = ! journalFormTrans.isJournalEnabled();
    confirmActive = RCashParameters::find().ConfirmationEnabled;

    rCashApprove.enabled(true);
    this.setApprovalButtons(false, true, false, false);

    rCashOrderPrint.enabled(ledgerJournalTrans_RCash.DocType != RCashDocType::ExchRateDifference &&  ledgerJournalTrans_RCash.DocType != RCashDocType::Correction);

    if (posted)
    {
        rCashApprove.enabled(false);
    }
    else
    {
        if (! confirmActive)
        {
            rCashTransConfirm.visible(false);
            rCashTransReject.visible(false);
            rCashTransApprove.visible(true);
            rCashTransApprove.enabled(true);
        }

        this.validateMarkedStatus(markedStatus);
    }

    if (! ledgerJournalTrans.parmLedgerDimension())
    {
        this.setApprovalButtons(false, false, false, false);
        rCashApprove.enabled(false);
    }
    // <GEECZ>
    if (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoCZ]))
    {
        element.enableButtonsActive_CZ();
    }
    // </GEECZ>
    // <GEEPL>
    if (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoPL]))
    {
        element.enableButtonsActive_PL();
    }
    // </GEEPL>
    element.enableButtonsActive_PL();
    // <GEEHU>
    if (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoHU]))
    {
        element.enableButtonsActive_HU();
    }
    // </GEEHU>

    ledgerJournalTrans_ds.allowEdit(ledgerJournalTrans_RCash.TransStatus   == RCashTransStatus::None);
    ledgerJournalTrans_ds.allowDelete(ledgerJournalTrans_RCash.TransStatus == RCashTransStatus::None);

    rCashDestinationTrans_ds.allowEdit(ledgerJournalTrans_RCash.TransStatus   == RCashTransStatus::None);
    rCashDestinationTrans_ds.allowDelete(ledgerJournalTrans_RCash.TransStatus == RCashTransStatus::None);
    rCashDestinationTrans_ds.allowCreate(ledgerJournalTrans_RCash.TransStatus == RCashTransStatus::None);

    // <GEERU>
    if (isCashCollEnabled)
    {
        element.enableCashCollButtonsActive_RU();
    }
    // </GEERU>
}

/// <summary>
/// Sets visibility of approval buttons based on markedStatus
/// </summary>
/// <param name = "_markedStatus">MarkedStatus of <c>LedgerJournalTrans datasource</c></param>
public void validateMarkedStatus(RCashTransStatus _markedStatus)
{
    switch (_markedStatus)
    {
        case RCashTransStatus::Confirmed:
            this.setApprovalButtons(true, false, true, true);
            break;

        case RCashTransStatus::Approved:
            this.setApprovalButtons(false, false, true, false);
            break;

        case RCashTransStatus::Canceled:
            rCashApprove.enabled(false);
            rCashTransConfirm.enabled(false);
            break;

        case RCashTransStatus::None:
            rCashOrderPrint.enabled(false);

        default:
            break;
    }
}

void enableButtonsActive_CZ()
{
    if (ledgerJournalTrans.AccountType == LedgerJournalACType::Ledger)
    {
        rCashApprove.enabled(false);
        rCashOrderPrint.enabled(false);
    }
}

public void enableButtonsActive_HU()
