    LedgerJournalTrans_RCash_RoundOffCash_HU.enabled(ledgerJournalTrans_RCash.ManualRounding_HU && ledgerJournalTrans_RCash.TransStatus == RCashTransStatus::None);
    LedgerJournalTrans_RCash_Rounding_HU.enabled(ledgerJournalTrans_RCash.TransStatus   == RCashTransStatus::None);
}

void enableButtonsActive_PL()
{
    Salestax_TaxBase_W.visible(! ledgerJournalTrans.vatPaymRefRecId_RU);
}

