{
    journalFormTrans = new LedgerJournalFormTrans_RCash(element);

    enableLedgerJournalTrans_W = LedgerJournalTrans_W::isLegalEntityInCountryRegion();

    super();

    LedgerJournalTrans_AccountNum.parmPostingType(LedgerPostingType::LedgerJournal);
    LedgerJournalTrans_AccountNumInfo.parmPostingType(LedgerPostingType::LedgerJournal);
    project_AccountNum.parmPostingType(LedgerPostingType::LedgerJournal);
    LedgerJournalTrans_OffsetAccount.parmPostingType(LedgerPostingType::LedgerJournal);
    LedgerJournalTrans_OffsetAccountInfo.parmPostingType(LedgerPostingType::LedgerJournal);
