    journalFormTrans.parmCtrlVoucherTab(ledgerJournalTrans_Voucher1);
    journalFormTrans.parmCtrlTransDateGrid(ledgerJournalTrans_TransDate);
    journalFormTrans.parmCtrlTransDateTab(ledgerJournalTrans_TransDate1);
    journalFormTrans.parmCtrlExchRate(currency_ExchRate);
    journalFormTrans.parmCtrlExchRateSecond(currency_ExchRateSecond);
    journalFormTrans.parmCtrlSettlementButton(settlementButton);
    journalFormTrans.parmCtrlSplitPostingButton(buttonSplitPosting);
    journalFormTrans.parmCtrlCourseButton(course);
    journalFormTrans.parmCtrlReportAsReadyButton(reportAsReady);
    journalFormTrans.parmCtrlApproveButton(approveJournal);
    journalFormTrans.parmCtrlRejectButton(reject);
    journalFormTrans.parmCtrlPostJournalMenu(posting);
    journalFormTrans.parmCtrlCheckJournalMenu(checking);
    journalFormTrans.parmCtrlVoucherTransButton(voucherTrans);
    journalFormTrans.parmCtrlBalanceControButton(balanceControl);
    journalFormTrans.parmCtrlGetTransactionsButton(getTransaction);
    journalFormTrans.parmCtrlFurtherPostingButton(ledgerJournalFurtherPosting);
    journalFormTrans.parmCtrlCopyPeriodicButton(copyPeriodicJournal);
    journalFormTrans.parmCtrlCashDueAnnouncementButton_RU(cashDueAnnouncement_RU);
    journalFormTrans.parmCtrlCancelCashDueAnnouncement_RU(cancelCashDueAnnouncement_RU);
    journalFormTrans.parmCtrlAccountDimensionButton(accountDimension);
    journalFormTrans.parmCtrlOffsetAccountDimensionButton(offsetAccountDimension);
    journalFormTrans.parmCtrlPaymFeeAccountDimensionButton(paymentFeeAccountDimension);
    journalFormTrans.formInitPost();

    this.initLedger();

    // Disable the company accounts field on the general tab if Intercompany transactions are disabled
    if (!isConfigurationkeyEnabled(configurationKeyNum(LedgerAdvIntercompanyAccounting)))
    {
        ledgerJournalTrans_Company.allowEdit(false);
    }
    cashDiscChanged             = false;
    // <GEELV>
    isCountryRegionLV = SysCountryRegionCode::isLegalEntityInCountryRegion([#isoLV]);
    // </GEELV>
    // <GEERU>
    isCashCollEnabled = LedgerJournalTrans_RCash::isCashCollEnabled_RU();
