void initLedger()
{
    TransDate   dateFrom    = dateNull();
    TransDate   dateTo      = DateTimeUtil::getSystemDate(DateTimeUtil::getUserPreferredTimeZone());

    if (element.args().dataset() == tableNum(LedgerJournalTable))
    {
        ledgerJournalTable = element.args().record();
        journalNum         = ledgerJournalTable.JournalNum;

        LedgerJournalTrans_AccountNum.parmJournalName(ledgerJournalTable.JournalName);
        LedgerJournalTrans_AccountNumInfo.parmJournalName(ledgerJournalTable.JournalName);
        project_AccountNum.parmJournalName(ledgerJournalTable.JournalName);
        CustPaymJournalFee_CustAccount.parmJournalName(ledgerJournalTable.JournalName);
        LedgerJournalTrans_OffsetAccount.parmJournalName(ledgerJournalTable.JournalName);
        LedgerJournalTrans_OffsetAccountInfo.parmJournalName(ledgerJournalTable.JournalName);

        // Lock the main account segment if "Fixed offset account" is selected in Journal Names
        if (ledgerJournalTrans.OffsetAccountType == LedgerJournalACType::Ledger)
        {
            LedgerJournalTrans_OffsetAccount.parmLockMainAccountSegment(ledgerJournalTable.FixedOffsetAccount);
            LedgerJournalTrans_OffsetAccountInfo.parmLockMainAccountSegment(ledgerJournalTable.FixedOffsetAccount);
        }

        if (ledgerJournalTable.FixedOffsetAccount && ledgerJournalTable.parmOffsetLedgerDimension())
        {
            switch (ledgerJournalTable.OffsetAccountType)
            {
                case LedgerJournalACType::Ledger:
                    if (DimensionFocusBalanceUpdateByLedgerFlight::instance().isEnabled())
                    {
                        MainAccountEventArgs args = MainAccountEventArgs::construct();
                        args.parmBalanceLedgerRecId(Ledger::current());

                        MainAccount::updateBalancesWithArgs(args);
                    }
                    else
                    {
                        MainAccount::updateBalances();
                    }

                    ledgerBalance = LedgerBalanceMainAccountAmounts::construct();
                    ledgerBalance.parmIncludeRegularPeriod(true);
                    ledgerBalance.parmIncludeClosingPeriod(true);
                    ledgerBalance.parmAccountingDateRange(dateFrom, dateTo);
                    ledgerBalance.calculateBalance(LedgerDimensionFacade::getMainAccountFromLedgerDimension(ledgerJournalTable.OffsetLedgerDimension));
                    balanceAmountMST = ledgerBalance.getAccountingCurrencyBalance();
                    break;

                case LedgerJournalACType::Bank:

                    bankSingleCurrency = BankAccountTable::isSingleCurrency(ledgerJournalTable.parmOffsetAccount(),
                                                                            ledgerJournalTable.JournalNum);

                    balanceAmountCur    = BankAccountTrans::sumTransCur(ledgerJournalTable.parmOffsetAccount());
                    balanceAmountMST    = BankAccountTrans::sumTransMST(ledgerJournalTable.parmOffsetAccount());

                    if (LedgerJournalName::find(ledgerJournalTable.JournalName).EndBalanceControl)
                    {
                        endBalance.visible(true);
                    }
                    break;

                case LedgerJournalACType::Cust:
                    balanceAmountMST    = CustTrans::balanceMST(ledgerJournalTable.parmOffsetAccount());
                    break;
                case LedgerJournalACType::Vend:
                    balanceAmountMST    = VendTrans::balanceMST(ledgerJournalTable.parmOffsetAccount());
                    break;
            }
            offsetBalance.visible(true);
            ledgerJournalTrans_OffsetAccountTypeInfo.allowEdit(false);
            /* <GEERU>
            ledgerJournalTrans_OffsetAccountInfo.allowEdit(false);
            </GEERU> */
            ledgerJournalTrans_OffsetAccountInfo.allowEdit(ledgerJournalTrans.OffsetAccountType == LedgerJournalACType::Ledger);
            ledgerJournalTrans_OffsetCompanyInfo.allowEdit(false);

            /* <GEERU>
            if (ledgerJournalTrans_OffsetAccount.allowEdit() == ledgerJournalTable.FixedOffsetAccount)
            {
                ledgerJournalTrans_OffsetAccount.allowEdit(!ledgerJournalTable.FixedOffsetAccount);
            }
           </GEERU> */

            if (ledgerJournalTable.FixedOffsetAccount)
            {
                ledgerJournalTrans_OffsetAccount.allowEdit(ledgerJournalTrans.OffsetAccountType == LedgerJournalACType::Ledger);
                ledgerJournalTrans_OffsetAccountInfo.allowEdit(ledgerJournalTrans.OffsetAccountType == LedgerJournalACType::Ledger);
            }
            else if (!ledgerJournalTrans_OffsetAccount.allowEdit())
            {
                ledgerJournalTrans_OffsetAccount.allowEdit(true);
            }
            else if (!ledgerJournalTrans_OffsetAccountInfo.allowEdit())
            {
                ledgerJournalTrans_OffsetAccountInfo.allowEdit(true);
            }
        }
        else
        {
            offsetBalance.visible(false);
        }
    }
}

