void refreshData(LedgerJournalTrans _findRecord)
{
    ledgerJournalEngine.newJournalActive(ledgerJournalTable, true);
    ledgerJournalTrans_ds.research();

    if (_findRecord)
    {
        ledgerJournalTrans_ds.positionToRecordByValue(fieldNum(LedgerJournalTrans, RecId), queryValue(_findRecord.RecId));
    }
    ledgerJournalTrans_ds.active();
    element.bookRefresh();
}

void research(Voucher _voucher        = '',
              boolean _clearTaxCache  = false,
              boolean _updateCashDisc = false)
{
    ledgerJournalEngine.newJournalActive(ledgerJournalTable, true);
    ledgerJournalTrans_ds.research();
    if (_clearTaxCache)
    {
        ledgerJournalEngine.clearTaxAmountCacheVoucher(_voucher);
    }

    if (_voucher)
    {
        ledgerJournalTrans_ds.positionToRecordByValue(fieldNum(LedgerJournalTrans, Voucher), _voucher);
    }

    if (_updateCashDisc)
    {
        ledgerJournalEngine.updateCashDisc(ledgerJournalTrans);
    }
}

void setDebCredProposal(boolean _init)
{
    if (_init)
    {
        debCredProposal = DebCredProposal::None;
    }

    if (debCredProposal != debCredProposalOld)
    {
        switch (debCredProposal)
        {
            case DebCredProposal::Credit  :
                ledgerJournalTrans_DS.object(fieldNum(LedgerJournalTrans, AmountCurDebit)).skip(true);
                break;

            case DebCredProposal::Debit   :
                ledgerJournalTrans_DS.object(fieldNum(LedgerJournalTrans, AmountCurCredit)).skip(true);
                break;

            case DebCredProposal::None    :
                ledgerJournalTrans_DS.object(fieldNum(LedgerJournalTrans, AmountCurCredit)).skip(false);
                ledgerJournalTrans_DS.object(fieldNum(LedgerJournalTrans, AmountCurDebit)).skip(false);
                break;
        }
        debCredProposalOld = debCredProposal;
    }
}

void setVisible()
{
    boolean isProject     = ledgerJournalTrans.AccountType ==  LedgerJournalACType::Project ||
                            ledgerJournalTrans.OffsetAccountType ==  LedgerJournalACType::Project;

    projectTab.visible(isProject);

    LedgerJournalTrans_Project_ActivityNumber.visible(isProject && isRCashInitFromOffsetAccountProjFlightEnabled);

    custVendPaymJournalFee_DS.allowCreate(ledgerJournalTrans.RecId);
}

public int task(int _taskId)
{
    #task
    int             ret;
    FormRun         formRun;
    MapEnumerator   childFormEnumerator;

    if (ledgerJournalEngine)
    {
        childFormEnumerator = ledgerJournalEngine.getChildFormsMap_W().getEnumerator();

        while (childFormEnumerator.moveNext())
        {
            formRun = childFormEnumerator.currentValue();
            if (! formRun.closed())
            {
                if (_taskId == #taskEsc)
                {
                    _taskId = #taskCtrlQ;
                }
            }
        }
    }

    ret = super(_taskId);

    return ret;
}

void updateJournalBalance()
{
    ledgerJournalTable.setJournalBalance(ledgerjournaltrans_ds.ledgerJournalBalance());
    element.bookRefresh();
}

