public void enableCashCollButtonsActive_RU()
{
    CashCollDeclarationCreate_RU.enabled(
        journalFormTrans.isJournalEnabled()                                 &&
        ledgerJournalTrans_RCash.TransStatus == RCashTransStatus::None      &&
        LedgerJournalTrans_RCash::isCashCollAllowEdit_RU(LedgerJournalTrans));

    CashCollReport_RU.enabled(
        (ledgerJournalTrans_RCash.TransStatus == RCashTransStatus::Confirmed ||
         ledgerJournalTrans_RCash.TransStatus == RCashTransStatus::Approved) &&
        LedgerJournalTrans_RCash::isCashCollAllowEdit_RU(LedgerJournalTrans));
}

public void enableCashCollFieldsActive_RU()
{
    boolean allowEditCashColl;

    allowEditCashColl = ledgerJournalTrans_ds.allowEdit() &&
                        ledgerJournalTrans_RCash_ds.allowEdit() &&
                        LedgerJournalTrans_RCash::isCashCollAllowEdit_RU(LedgerJournalTrans);

    ledgerJournalTrans_RCash_ds.object(fieldNum(LedgerJournalTrans_RCash, CashCollBagNum_RU)).allowEdit(allowEditCashColl);
    ledgerJournalTrans_RCash_ds.object(fieldNum(LedgerJournalTrans_RCash, CashCollStoreId_RU)).allowEdit(allowEditCashColl);

    ledgerJournalTrans_ds.object(fieldNum(LedgerJournalTrans, BankRespWorkerId_RU)).allowEdit(allowEditCashColl);

    ledgerJournalTrans_RCash_ds.object(fieldNum(LedgerJournalTrans_RCash, CashCollDebitAccountNum_RU)).allowEdit(allowEditCashColl);
    ledgerJournalTrans_RCash_ds.object(fieldNum(LedgerJournalTrans_RCash, CashCollCreditAccountNum_RU)).allowEdit(allowEditCashColl);
    ledgerJournalTrans_RCash_ds.object(fieldNum(LedgerJournalTrans_RCash, CashCollBankContributor_RU)).allowEdit(allowEditCashColl);

    ledgerJournalTrans_ds.object(fieldNum(LedgerJournalTrans, BankSpecAccountId_RU)).allowEdit(allowEditCashColl);

    RetailCashCollDeclarationJourTrans_RU_DS.allowEdit(allowEditCashColl);
}

