void modified()
{
    super();

    ledgerJournalEngine.clearTaxAmountCache(ledgerJournalTrans);

    if (isRCashInitFromOffsetAccountProjFlightEnabled)
    {
        ledgerJournalEngine.initFromOffsetAccount(ledgerJournalTrans, ledgerJournalTrans_Project);
    }
    else
    {
        ledgerJournalEngine.initFromOffsetAccount(ledgerJournalTrans);
    }

    ledgerJournalEngine.cashDiscCodeModified(ledgerJournalTrans);
    LedgerJournalEngine::setProjCostSalesPrice(ledgerJournalTrans, ledgerJournalTrans_Project, ledgerJournalEngine.parmSalesPrice());
    ledgerJournalEngine.setProjAmount(ledgerJournalTrans, ledgerJournalTrans_Project);
    ledgerJournalEngine.setCashRegisterTerminal_W(ledgerJournalTrans);
    element.setVisible();

    // <GEERU>
    if (isCashCollEnabled)
    {
        ledgerJournalEngine.offsetAccountModifiedCashColl_RU(ledgerJournalTrans, LedgerJournalTrans_RCash);
    }
    // </GEERU>

    element.enableDisableCashRegister_W();

    ledgerJournalTrans_DS.rereadReferenceDataSources();
    // <GEELV>
    if (isCountryRegionLV)
    {
        ledgerJournalTransPaymTransCode.initFromLedgerJournalTrans(ledgerJournalTrans);
        LedgerJournalTransPaymTransCode_DS.refresh();
    }
    // </GEELV>

}

