public boolean validate()
{
    boolean             ret;
    LvCashReceiptTable  lvCashReceiptTable;

    ret = super();
    if (ret && ledgerJournalTrans_Rcash.LvCashReceiptTable)
    {
        lvCashReceiptTable = LvCashReceiptTable::find(ledgerJournalTrans_Rcash.LvCashReceiptTable);
        if (ledgerJournalTrans.TransDate < lvCashReceiptTable.AcquisitionDate)
        {
            ret = checkFailed("@GLS113251");
        }
    }

    LedgerJournalTrans_AccountNum.parmControlDate(ledgerJournalTrans.TransDate);
    LedgerJournalTrans_AccountNumInfo.parmControlDate(ledgerJournalTrans.TransDate);
    project_AccountNum.parmControlDate(ledgerJournalTrans.TransDate);
    LedgerJournalTrans_OffsetAccount.parmControlDate(ledgerJournalTrans.TransDate);
    LedgerJournalTrans_OffsetAccountInfo.parmControlDate(ledgerJournalTrans.TransDate);

    return ret;
}

