public void executeQuery()
{
    select officialsTrans_RU
        where officialsTrans_RU.ReportType   == OffReportType_RU::CashSlip      &&
              officialsTrans_RU.OffSessionId == ledgerJournalTrans.OffSessionId_RU;
    super();
}

