display AmountCur amountRemain()
{
    AmountCur   totalAllocated =
                    (select sum(AmountCur) from rCashDestinationTrans
                        where rCashDestinationTrans.LedgerJournalTransRecId == ledgerJournalTrans.RecId).AmountCur;

    return totalAllocated ? abs(ledgerJournalTrans.amount()) - totalAllocated : 0.0;
}

