void research(Voucher _voucher        = '',
              boolean _clearTaxCache  = false,
              boolean _updateCashDisc = false)
{
    ledgerJournalEngine.newJournalActive(ledgerJournalTable, true);
    ledgerJournalTrans_ds.research();
    if (_clearTaxCache)
    {
        ledgerJournalEngine.clearTaxAmountCacheVoucher(_voucher);
    }

    if (_voucher)
    {
        ledgerJournalTrans_ds.positionToRecordByValue(fieldNum(LedgerJournalTrans, Voucher), _voucher);
    }

    if (_updateCashDisc)
    {
        ledgerJournalEngine.updateCashDisc(ledgerJournalTrans);
    }
}

void setDebCredProposal(boolean _init)
{
    if (_init)
    {
        debCredProposal = DebCredProposal::None;
    }

    if (debCredProposal != debCredProposalOld)
    {
        switch (debCredProposal)
        {
            case DebCredProposal::Credit  :
                ledgerJournalTrans_DS.object(fieldNum(LedgerJournalTrans, AmountCurDebit)).skip(true);
                break;

            case DebCredProposal::Debit   :
                ledgerJournalTrans_DS.object(fieldNum(LedgerJournalTrans, AmountCurCredit)).skip(true);
                break;

            case DebCredProposal::None    :
                ledgerJournalTrans_DS.object(fieldNum(LedgerJournalTrans, AmountCurCredit)).skip(false);
                ledgerJournalTrans_DS.object(fieldNum(LedgerJournalTrans, AmountCurDebit)).skip(false);
                break;
        }
        debCredProposalOld = debCredProposal;
    }
}

