    super();

    ledgerJournalTransRange   = this.query().dataSourceNo(1).addRange(fieldNum(LedgerJournalTrans, Invisible));
    ledgerJournalTransRange.value(strFmt('%1',0));

    ledgerJournalEngine = new LedgerJournalEngine_RDeferrals(element);
