    }

    return postalAddressLocation;
}

public void refreshLedgerJournalTrans_IN()
{
    ledgerJournalTrans_ds.reread();
    ledgerJournalTrans_ds.refresh();
}

public void refreshProposal()
{
    ledgerJournalTrans_DS.research();
}

public void setDebCredProposal(boolean _init)
{
    if (_init)
    {
        debCredProposal = DebCredProposal::None;
    }

    if (debCredProposal != debCredProposalOld)
    {
        switch (debCredProposal)
        {
            case DebCredProposal::Credit  :
                ledgerJournalTrans_AmountCurDebit.skip(true);
                amountCurDebit.skip(true);
                break;

            case DebCredProposal::Debit   :
                ledgerJournalTrans_AmountCurCredit.skip(true);
                amountCurCredit.skip(true);
                break;

            case DebCredProposal::None    :
                ledgerJournalTrans_AmountCurCredit.skip(false);
                amountCurCredit.skip(false);
                ledgerJournalTrans_AmountCurDebit.skip(false);
                amountCurDebit.skip(false);
                break;
        }
        debCredProposalOld = debCredProposal;
    }
}

public void enableElectronicPaymentFields_BR()
{
    VendPaymModeTable vendPaymModeTable = VendPaymModeTable::find(ledgerJournalTrans.PaymMode);

    var enableElectronicPaymFields = vendPaymModeTable.isElectronicPayment_BR();

    vendEPRemitInfo_BR_ds.object(fieldnum(VendEPRemitInfo_BR,TypedLine)).allowEdit(enableElectronicPaymFields);
    vendEPRemitInfo_BR_ds.object(fieldnum(VendEPRemitInfo_BR,BarCodeInfo)).allowEdit(enableElectronicPaymFields);

    var enableConfiguratorFields = vendPaymModeTable.ExportLayoutGroupId_BR != '';

    vendEPRemitInfo_BR_ds.object(fieldnum(VendEPRemitInfo_BR,DocumentType)).visible(enableConfiguratorFields);
    vendEPRemitInfo_BR_ds.object(fieldnum(VendEPRemitInfo_BR,BankFolder)).visible(enableConfiguratorFields);
    vendEPRemitInfo_BR_ds.object(fieldnum(VendEPRemitInfo_BR,Instruction_1)).visible(enableConfiguratorFields);
    vendEPRemitInfo_BR_ds.object(fieldnum(VendEPRemitInfo_BR,Instruction_2)).visible(enableConfiguratorFields);
}

public void setPaymentByLetterOfCredit()
{
    if (ledgerJournalTrans.getLedgerJournalTransBankLC().BankLCLine)
    {
        ledgerJournalTrans_ds.object(fieldNum(LedgerJournalTrans, PaymMode)).allowEdit(false);
        ledgerJournalTrans_ds.object(fieldNum(LedgerJournalTrans, PaymSpec)).allowEdit(false);
        ledgerJournalTrans_ds.object(fieldNum(LedgerJournalTrans, Prepayment)).allowEdit(false);
    }
}

public void setRemitteeCurrency()
{
    boolean enableField = false;

    if (ledgerJournalTrans.PaymMode && ledgerJournalTrans.PaymSpec)
    {
        if (remitteeCurrencyMap.exists([ledgerJournalTrans.PaymMode, ledgerJournalTrans.PaymSpec]))
        {
            enableField = remitteeCurrencyMap.lookup([ledgerJournalTrans.PaymMode, ledgerJournalTrans.PaymSpec]);
        }
        else
        {
            remitteeCurrencyMap.insert([ledgerJournalTrans.PaymMode, ledgerJournalTrans.PaymSpec], false);
            enableField = false;
        }
    }
    if (enableField)
    {
        ledgerJournalTrans_RemitteeCurrency.enabled(true);
    }
    else
    {
        ledgerJournalTrans_RemitteeCurrency.enabled(false);
        ledgerJournalTrans.RemitteeCurrency = '';
    }
}

public void setTaxWithholdEnabled_IN()
{
    ledgerJournalTransTaxExtensionIN_ds.object(fieldNum(LedgerJournalTransTaxExtensionIN, tdsGroup)).enabled((!ledgerJournalTransTaxExtensionIN.tcsGroup && !ledgerJournalTrans.TaxWithholdGroup));
    ledgerJournalTransTaxExtensionIN_ds.object(fieldNum(LedgerJournalTransTaxExtensionIN, tcsGroup)).enabled((!ledgerJournalTransTaxExtensionIN.tdsGroup && !ledgerJournalTrans.TaxWithholdGroup));
    ledgerJournalTrans_ds.object(fieldNum(LedgerJournalTrans, TaxWithholdGroup)).enabled((!ledgerJournalTransTaxExtensionIN.tdsGroup && !ledgerJournalTransTaxExtensionIN.tcsGroup));
    boolean taxWithholdEnabled = ledgerJournalTransTaxExtensionIN.tdsGroup || ledgerJournalTransTaxExtensionIN.tcsGroup;

    TaxWithholdTrans_IN.enabled(taxWithholdEnabled);

    if (ledgerJournalTable.Posted)
    {
        taxWitholdChallan_IN.enabled(true);
    }
}

public void setTaxWithholdVisible_IN()
{
    TaxWithholdParameters_IN    taxWithholdParameters;

    taxWithholdParameters = TaxWithholdParameters_IN::find();
    ledgerJournalTransTaxExtensionIN_ds.object(fieldNum(LedgerJournalTransTaxExtensionIN, tdsGroup)).visible(taxWithholdParameters.tdsPayment);
    ledgerJournalTransTaxExtensionIN_ds.object(fieldNum(LedgerJournalTransTaxExtensionIN, tcsGroup)).visible(taxWithholdParameters.tcsPayment);
    taxWithhold_IN.visible(taxWithholdParameters.tdsPayment || taxWithholdParameters.tcsPayment);
}

private void setVisible_IN()
{
    boolean             isTaxParametersMarked   = TaxParameters::checkTaxParameters_IN();

    challanInformation_IN.visible(isTaxParametersMarked);
    updatePLA.visible(taxParametersLoc.Excise_IN);
    plaVoucher_IN.visible(taxParametersLoc.Excise_IN);
    ledgerJournalTransTaxExtensionIN_AssessableValueTransactionCurrency.visible(isTaxParametersMarked);
    ledgerJournalTransTaxExtensionIN_MaximumRetailPrice.visible(isTaxParametersMarked);
    defermentSchedule_IN.visible(taxParametersLoc.vat_in);

    TaxGSTSummaryDetails_IN.visible(TaxSolutionScopeIntegrationUtil::isCompanyEnabled());

    boolean shouldGSTTDSVisible = TaxSolutionScopeIntegrationUtil::isCompanyEnabled();
    ledgerJournalTransTaxExtensionIN_ds.object(fieldNum(LedgerJournalTransTaxExtensionIN, InvoiceSettleAmountGSTTDS)).visible(shouldGSTTDSVisible);
    ledgerJournalTransTaxExtensionIN_ds.object(fieldNum(LedgerJournalTransTaxExtensionIN, ExcludeGSTAmountGSTTDS)).visible(shouldGSTTDSVisible);
    ledgerJournalTransTaxExtensionIN_ds.object(fieldNum(LedgerJournalTransTaxExtensionIN, TaxableAmountGSTTDS)).visible(shouldGSTTDSVisible);
}

