/// <summary>
/// Filters data of the grid by given payment message line type.
/// </summary>
private void filterGridByPaymentMessageLineType_DE()
{
    if (PaymentMessageLineType.selection() == PaymentMessageLineType_DE::TransitTrade)
    {
        ServiceGroup.visible(false);
        TransitTradeGroup.visible(true);
    }
    else
    {
        ServiceGroup.visible(true);
        TransitTradeGroup.visible(false);
    }

    QueryBuildDataSource qbds = PaymentMessageLines_DE_DS.query().dataSourceTable(tableNum(PaymentMessageLines_DEDTAZV));
    SysQuery::findOrCreateRange(qbds, fieldNum(PaymentMessageLines_DEDTAZV, PaymentMessageLineType)).value(queryValue(PaymentMessageLineType.selection()));
    PaymentMessageLines_DE_DS.executeQuery();
}

