            break;
        case LedgerJournalACType::Vend :
            VendTable::jumpRefVendor(ledgerJournalTrans.parmOffsetAccount(), ledgerJournalTrans.Company);
            break;
        default :
            LedgerJournalTrans_OffsetAccount.jumpRef();
    }
}

