{
    boolean ret;

    ret = super();

    if (ledgerJournalTrans.Prepayment &&
        VendParameters::find().TaxOnPrepayment &&
        !ledgerJournalTrans.ledgerJournalTable().LedgerJournalInclTax)
    {
        error ("@SYS98399");
        ret = false;
    }

    return ret;
}

