    AssetBook::lookupOpenBookIdForAsset(_formControl,
                                        ledgerJournalTrans_Asset.AssetId,
                                        true,  // CurrentOperationsTax must be considered
                                        ledgerJournalTable.CurrentOperationsTax,
                                        ledgerJournalTrans_Asset.Company,
                                        true);
}

