public void clicked()
{
    LedgerJournalTrans currentRecord;
    currentRecord.data(ledgerJournalTrans);

    if (element.shouldExecPaymReconciliationRejectClickMethod(LedgerJournalTrans))
    {
        super();

        // <GEERU>
        if (countryRegion_RU)
        {
            ledgerJournalEngine.refreshWhenPosted();
            journalFormTrans.enableButtonsActive();
            element.bookRefresh();
        }

        if (!countryRegion_RU
            || (countryRegion_RU
            && !ledgerJournalTrans.BankCurrencyTransferLog_RU
            && !ledgerJournalTrans.existBankPaymentOrderJour_RU()))
        {
            // </GEERU>
            paymReconciliationNone.enabled(true);
            paymReconciliationConfirm.enabled(true);
            paymReconciliationRecieved.enabled(true);
            paymReconciliationSent.enabled(true);
            // <GEERU>
        }
        // </GEERU>

        //Refactor: Following method execution will no longer be needed after the Deliverable 3690207 is addressed.
        ledgerJournalTrans_DS.positionToRecord(currentRecord);
    }
}

