public void lookup()
{
    QueryBuildDataSource queryBuildDataSource;
    SysTableLookup       sysTableLookup = SysTableLookup::newParameters(tablenum(TaxTransactionIdInformation_IN),this,true);
    Query                query = new Query();

    sysTableLookup.addLookupfield(fieldnum(TaxTransactionIdInformation_IN, TransactionID));

    queryBuildDataSource = query.addDataSource(tablenum(TaxTransactionIdInformation_IN));
    queryBuildDataSource.addSortField(fieldnum(TaxTransactionIdInformation_IN, TransactionID));
    queryBuildDataSource.addRange(fieldnum(TaxTransactionIdInformation_IN, TaxType)).value(enum2str(TaxType_IN::GST));
    queryBuildDataSource.orderMode(ordermode::GroupBy);

    sysTableLookup.parmQuery(query);
    sysTableLookup.performFormLookup();
}

public boolean modified()
{
    boolean ret;

    TaxTransactionIdInformation_IN transactionIdInformation = TaxTransactionIdInformation_IN::findByTransactionId(this.text());
    LedgerJournalTransTaxExtensionIN.OriginalTransactionDate = transactionIdInformation.Date_GST;

    ret = super();

    return ret;
}

