public void lookup()
{
    custVendTransPaymRef_RU = LedgerJournalTrans_W::lookupPrePayment(this, FactureTaxCorrectionContragentType_RU::Vendor, false, ledgerJournalTrans.parmAccount());
}

public boolean modified()
{
    boolean ret = super();

    if (ret)
    {
        if (custVendTransPaymRef_RU)
        {
            ledgerJournalTrans_W.initPrePaymRef(custVendTransPaymRef_RU);
            custVendTransPaymRef_RU = null;
        }
        else
        {
            ledgerJournalTrans_W.clearPrePaymRef();
        }

        LedgerJournalTrans_W_ds.refresh();
    }

    return ret;
}

