/// <summary>
/// Updates the form when the account number field is modified.
/// </summary>
public void accountNumModifiedPost()
{
    changecompany(ledgerJournalTrans.Company)
    {
        debCredProposal = ledgerJournalEngine.accountModified(ledgerJournalTrans);

        element.setDebCredProposal(false);

        ledgerJournalEngine.clearTaxAmountCache(ledgerJournalTrans);

        ledgerJournalTrans_ds.refresh();

        journalFormTrans.enableButtonsActive();
    }
}

/// <summary>
/// Handles the event that fires when focus is given to the form.
/// </summary>
/// <param name="_active">
/// true if active; otherwise, false.
/// </param>
public void activate(boolean _active)
{
    super(_active);
    journalFormTrans.manageEditingModeForApprovedWFApproval();
}

public void bookRefresh(boolean _isClosing = false)
{
    LedgerIJournalFormRefreshable callingForm = element.args().caller() as LedgerIJournalFormRefreshable;

    if (callingForm)
    {
        callingForm.refresh();
    }

    if (!_isClosing)
    {
        ledgerJournalTrans_ds.reread();
    }
}

public void callerRefresh()
{
    ledgerJournalEngine.clearCorrectedTaxAmount(ledgerJournalTrans);
    ledgerJournalTrans_ds.refresh();
}

public void close()
{
    ledgerJournalEngine.close();
    ledgerJournalEngine.formMethodClosePre();
    journalFormTrans.formClosePre();
    element.bookRefresh(true);

    super();
}

/// <summary>
/// Set the AccountDimension button's display menu item to point to the correct form.
/// Set the OffsetAccountDimension button's display menu item to point to the correct form.
/// Set the PaymentFeeAccountDimension button's display menu item to point to the correct form.
/// Set the PostJournal button's action menu item to point to the correct form.
/// Set the LedgerJournalPostTransfer button's action menu item to point to the correct form.
/// Set the CheckJournal button's action menu item to point to the correct form.
/// Set the CheckVoucher button's action menu item to point to the correct form.
/// Set the buttonDeleteTransactions button's action menu item to point to the correct form.
/// Set the buttonGetTransactions button's action menu item to point to the correct form.
/// </summary>
/// <remarks>
/// Display menuitem name changed depending on the caller menuitem of this form.
/// </remarks>
public void enableButtons()
{
    if (element.args())
    {
        switch (element.args().menuItemName())
        {
            case menuitemDisplayStr(LedgerJournalTransVendRemittance):
                accountDimension.menuItemName(menuitemDisplayStr(LedgerJourTransDimLJJourTrnVendPrNtRemit));
                offsetAccountDimension.menuItemName(menuitemDisplayStr(LedgerJourTROffDimLJJourTrnVendPrNtRemit));
                paymentFeeAccountDimension.menuItemName(menuitemDisplayStr(LedgerJourTRPayDIMLJJourTrnVendPrNtRemit));
                postJournal.menuItemName(menuitemActionStr(LedgerJourPostLJTransVendPrNoteRemit));
                ledgerJournalPostTransfer.menuItemName(menuitemActionStr( LedgerJourPostTrLJTransVendPrNoteRemit));
                checkJournal.menuItemName(menuitemActionStr(LedgerJourChkLJTransPrNtRemit));
                checkVoucher.menuItemName(menuitemActionStr(LedgerJourChk1LJTransPrNtRemit));
                buttonDeleteTransactions.menuItemName(menuitemActionStr(LedgerJourDelTransLJTransVendPrNoteRemit));
                buttonGetTransactions.menuItemName(menuitemActionStr(LedgerJourGetTransLJTransVendPrNoteRemit));
                break;

            case menuitemDisplayStr(LedgerJournalTransVendNoteSettle):
                accountDimension.menuItemName(menuitemDisplayStr(LedgerJourTransDimLJJourTrnVendPrNtSetl));
                offsetAccountDimension.menuItemName(menuitemDisplayStr(LedgerJourTROffDimLJJrTrnVendPrNtSettle));
                paymentFeeAccountDimension.menuItemName(menuitemDisplayStr(LedgerJourTRPayDIMLJJrTrnVendPrNtSettle));
                postJournal.menuItemName(menuitemActionStr(LedgerJourPostLJTransVendPrNoteSettle));
                ledgerJournalPostTransfer.menuItemName(menuitemActionStr(LedgerJourPostTrLJTransVendPrNoteSettle));
                checkJournal.menuItemName(menuitemActionStr(LedgerJourChkLJTransPrNtSettle));
                checkVoucher.menuItemName(menuitemActionStr(LedgerJourChk1LJTransPrNtSettle));
                buttonDeleteTransactions.menuItemName(menuitemActionStr(LedgerJourDelTransLJTransVendPrNtSettle));
                buttonGetTransactions.menuItemName(menuitemActionStr(LedgerJourGetTransLJTransVendPrNtSettle));
                break;

            case menuitemDisplayStr(LedgerJournalTransVendNoteRedraw):
                accountDimension.menuItemName(menuitemDisplayStr(LedgerJourTransDimLJJourTrnVendPrNtRedrw));
                offsetAccountDimension.menuItemName(menuitemDisplayStr(LedgerJourTROffDimLJJourTrnVendPrNtRedrw));
                paymentFeeAccountDimension.menuItemName(menuitemDisplayStr(LedgerJourTRPayDIMLJJrTrnVendPrNtRedraw));
                postJournal.menuItemName(menuitemActionStr(LedgerJourPostLJTransVendPrNoteRedraw));
                ledgerJournalPostTransfer.menuItemName(menuitemActionStr( LedgerJourPostTrLJTransVendPrNoteRedraw));
                checkJournal.menuItemName(menuitemActionStr(LedgerJourChkLJTransPrNtRedraw));
                checkVoucher.menuItemName(menuitemActionStr(LedgerJourChk1LJTransPrNtRedraw));
                buttonDeleteTransactions.menuItemName(menuitemActionStr(LedgerJourDelTransLJTrnsVendPrNoteRedraw));
                buttonGetTransactions.menuItemName(menuitemActionStr(LedgerJourGetTransLJTrnsVendPrNoteRedraw));
                break;

            case menuitemDisplayStr(LedgerJournalTransVendNoteDraw):
                accountDimension.menuItemName(menuitemDisplayStr(LedgerJourTransDimLdJourTrnVendPrNtDraw));
                offsetAccountDimension.menuItemName(menuitemDisplayStr(LedgerJourTROffDimLJJourTrnVendPrNtDraw));
                paymentFeeAccountDimension.menuItemName(menuitemDisplayStr(LedgerJourTRPayDIMLJJourTrnVendPrNtDraw));
                postJournal.menuItemName(menuitemActionStr(LedgerJourPostLJTransVendPrNoteDraw));
                ledgerJournalPostTransfer.menuItemName(menuitemActionStr(LedgerJourPostTrLJTransVendPrNoteDraw));
                checkJournal.menuItemName(menuitemActionStr(LedgerJourChkLJTransPrNtDraw));
                checkVoucher.menuItemName(menuitemActionStr(LedgerJourChk1LJTransPrNtDraw));
                buttonDeleteTransactions.menuItemName(menuitemActionStr(LedgerJourDelTransLJTransVendPrNoteDraw));
                buttonGetTransactions.menuItemName(menuitemActionStr(LedgerJourGetTransLJTransVendPrNoteDraw));
