                accountDimension.enabled(false);
                offsetAccountDimension.enabled(false);
                paymentFeeAccountDimension.enabled(false);
                postJournal.enabled(false);
                ledgerJournalPostTransfer.enabled(false);
                checkJournal.enabled(false);
                checkVoucher.enabled(false);
                buttonDeleteTransactions.enabled(false);
                buttonGetTransactions.enabled(false);
                break;
