public int active()
{
    int ret = super();

    CustVendPaymJournalFee_CustVendAC.parmJournalName(ledgerJournalTable.JournalName);
    CustVendPaymJournalFee_CustVendAC.parmCurrency(custVendPaymJournalFee.FeeCurrency);
    CustVendPaymJournalFee_CustVendAC.parmControlDate(ledgerJournalTrans.TransDate);

    return ret;
}

public void modified()
{
    super();

    // Clear the account when the account type changes
    custVendPaymJournalFee.parmLedgerDimension(0);

    if (custVendPaymJournalFee.LedgerJournalACType == LedgerJournalACType::Ledger ||
        custVendPaymJournalFee.LedgerJournalACType == LedgerJournalACType::Vend)
    {
        custVendPaymJournalFee_CustVendAC.enabled(true);
    }
    else
    {
        // Disable the account lookup if the value is not Ledger or Vendor
        custVendPaymJournalFee_CustVendAC.enabled(false);
    }
    custVendPaymJournalFee_ds.rereadReferenceDataSources();
}

/// <summary>
/// Checks whether this data sources should be enable or not.
/// </summary>
/// <returns>
/// true if it should be enabled; otherwise, return false.
/// </returns>
/// <remarks>
/// This data source is joined to its parent data source with join type <c>Passive</c>
/// by default. It means this data source is disabled by default.
/// If any related feature needs this data source, then add condition(s) here to
/// mark that it should be enabled.
/// </remarks>
public boolean isNeeded()
{
    LedgerJournalTable ledgerJournalTableLocal = element.args().record();

    boolean ret = TaxThaiGovCertificationFeatureChecker::isAverageVATEnabled()
        && ledgerJournalTableLocal.JournalType == LedgerJournalType::VendPromissoryNoteSettle;

    return ret;
}

public boolean validateWrite()
{
    boolean ret = true;

    if (LedgerJournalTransExtensionTH.LedgerJournalTrans)
    {
        ret = super();
    }

    return ret;
}

public void write()
{
    if (LedgerJournalTransExtensionTH.LedgerJournalTrans)
    {
        super();
    }
}

public void init()
{
    if (this.isNeeded())
    {
        this.linkType(FormLinkType::OuterJoin);
    }

    super();
}

public void initValue()
{
    super();

    ledgerJournalTransTaxExtensionIN.TaxModelDocLineExtensionIN::init(ledgerJournalTrans);
}

/// <summary>
/// Checks whether ExtensionTable needs to be populated
/// </summary>
/// <returns>
/// Returns true if country region is India
/// </returns>
public boolean isNeeded()
{
    return SysCountryRegionCode::isLegalEntityInCountryRegion([#isoIN]);
}

public void init()
{
    if (this.isNeeded())
    {
        this.linkType(FormLinkType::InnerJoin);
    }
    super();
}

public void clicked()
{
    ledgerJournalEngine.clearVoucher();

    try
    {
        super();
    }
    finally
    {
        journalFormTrans.parmCheckPostResults(ledgerJournalEngine.getPostingResults());

        if (journalFormTrans.parmCheckPostResults().errorInJournal())
        {
            fieldError.visible(true);
        }

        ledgerJournalEngine.refreshWhenPosted();
        journalFormTrans.enableButtonsActive();
        element.bookRefresh();
    }
}

