public void clicked()
{
    FormRun formRun;
    Args args = new Args();
    boolean end = false;

    super();

    if (!ledgerJournalTrans.parmLedgerDimension())
    {
        throw error("@SYS25989");
    }

    args.record(ledgerJournalTrans);

    switch (ledgerJournalTrans.AccountType)
    {
        case LedgerJournalACType::Cust :
            formRun = new MenuFunction(menuitemDisplayStr(CustShowSpecifikation),MenuItemType::Display).create(args);
            break;

        case LedgerJournalACType::Vend :
            formRun = new MenuFunction(menuitemDisplayStr(VendShowSpecLdgrJourTrnVendPrmNt),MenuItemType::Display).create(args);
            break;

        default:
            end = true;
    }

    if (!end)
    {
        if (formRun)
        {
            formRun.run();
            formRun.wait();
        }
    }
}

