void bookRefresh(boolean _isClosing = false)
{
    ledgerJournalTable_ds.reread();
    ledgerJournalTable_ds.refresh();

    journalFormTable.datasourceReReadPost();

    if (!_isClosing)
    {
        ledgerJournalTrans_ds.reread();
    }
}

void callerRefresh()
{
    ledgerJournalEngine.clearCorrectedTaxAmount(ledgerJournalTrans);
    ledgerJournalTrans_ds.refresh();
}

public boolean canClose()
{
    boolean canClose;

    try
    {
        // Signal that the close event is beginning.
        ledgerJournalTransEntityManager.getFormDataEventManager().beginFormCloseEvent();

        canClose = super();

        // Signal that the close event is finished.
        ledgerJournalTransEntityManager.getFormDataEventManager().endFormCloseEvent();
    }
    catch (Exception::UpdateConflict)
    {
        // This exception should not be handled by the generic handling logic below.

        // Signal that the close event is finished.
        ledgerJournalTransEntityManager.getFormDataEventManager().endFormCloseEvent();

        // Chain the exception forward.
        throw Exception::UpdateConflict;
    }
    catch
    {
        // Signal that the close event is finished.
        ledgerJournalTransEntityManager.getFormDataEventManager().endFormCloseEvent();

        // Chain the exception forward.
        throw Exception::Error;
    }

    return canClose;
}

void close()
{
    //Line
    ledgerJournalEngine.close();
    ledgerJournalEngine.formMethodClosePre();
    journalFormTrans.formClosePre();
    element.bookRefresh(true);

    //Header
    journalFormTable.formClosePre();

    super();

    classfactory.lastValuePut(this.pack(), curext(), curUserId(), UtilElementType::Form, formStr(LedgerJournalVoucher_CN));
}

public void enableBankLC()
{
    bankLCEnabled = BankLCImportFeatureChecker::checkBankLCImportEnabled()
        || BankLCExportFeatureChecker::checkBankLCExportEnabled();

    if (bankLCEnabled)
    {
        ledgerJournalTransBankLC_ds.allowEdit(true);
        bankLCGroup.visible(true);

        bankLCImportNumberController = BankLCImportNumberController::construct(true);
        bankLCImportNumberController.parmBankLCImportStatus(BankLCImportStatusType::Confirmed);
        bankLCImportNumberController.parmReturnAsBankLC(false);
    }
    else
    {
        ledgerJournalTransBankLC_ds.allowEdit(false);
        bankLCGroup.visible(false);
    }
}

/// <summary>
/// Enables and disables controls on the form.
/// </summary>
/// <remarks>
/// This method is called from the enableButtonsActive method of the JournalFormTable class. The
/// JournalFormTable.enableButtonsActive method is called by the active method on the data source
/// of this form.
/// </remarks>
public void enableButtonsActive()
{
    LedgerJournalType journalType = journalFormTable.journalTypeId();
    UserGroupId approveGroupId = ledgerJournalTable.ledgerJournalName().ApproveGroupId;

    printMenu.enabled(ledgerJournalTable.RecId != 0);

    switch (journalType)
    {
        case LedgerJournalType::Periodic  :
            checkJournal.visible(false);
            postJournal.visible(false);
            course.visible(false);
            break;

        case LedgerJournalType::PurchaseLedger :
            break;

        case LedgerJournalType::Daily :
            checkJournal.visible(true);
            postJournal.visible(true);
            post.menuItemName(menuitemActionStr(LedgerJourPostLJTransDaily));
            ledgerJournalPostTransfer.menuItemName(menuitemActionStr(LedgerJourPostTrLJTransDaily));
            checkJournal.menuItemName(menuitemActionStr(LedgerJourChkLedgerJourTransDaily));
            break;

        case LedgerJournalType::Allocation :
            checkJournal.visible(true);
            postJournal.visible(true);

            // only posting supported not post and transfer
            ledgerJournalPostTransfer.visible(false);

            // dimensions are not editable
            tabFinancialDimensions.enabled(false);

        case LedgerJournalType::Elimination :
            checkJournal.visible(true);
            postJournal.visible(true);

            // only posting supported not post and transfer
            ledgerJournalPostTransfer.visible(false);

            // dimensions are not editable
            tabFinancialDimensions.enabled(false);

        case LedgerJournalType::Approval :
            checkJournal.visible(true);
            postJournal.visible(true);
            break;

        case LedgerJournalType::Payment :
            checkJournal.visible(true);
            postJournal.visible(true);
            break;

        case LedgerJournalType::CustPayment :
            checkJournal.visible(true);
            postJournal.visible(true);
            break;

        case LedgerJournalType::Cost  :
            checkJournal.visible(true);
            postJournal.visible(true);
            break;

        case LedgerJournalType::VendInvoiceRegister :
            checkJournal.visible(true);
            postJournal.visible(true);
            break;

        case LedgerJournalType::Assets :
            checkJournal.visible(true);
            postJournal.visible(true);
            break;

        case LedgerJournalType::AssetBudgets :
            checkJournal.visible(true);
            postJournal.visible(false);
            break;

        case LedgerJournalType::CustPaymRemittance :
            checkJournal.visible(true);
            postJournal.visible(true);
            break;

        case LedgerJournalType::VendPaymRemittance :
            checkJournal.visible(true);
            postJournal.visible(true);
            break;

        case LedgerJournalType::CustBillOfExchangeDraw :
            checkJournal.visible(true);
            postJournal.visible(true);
            break;

        case LedgerJournalType::CustBillOfExchangeProtest :
            checkJournal.visible(true);
            postJournal.visible(true);
            break;

        case LedgerJournalType::CustBillOfExchangeRedraw :
            checkJournal.visible(true);
            postJournal.visible(true);
            break;

        case LedgerJournalType::CustBillOfExchangeSettle :
            checkJournal.visible(true);
            postJournal.visible(true);
            break;

        case LedgerJournalType::VendPromissoryNoteDraw :
            checkJournal.visible(true);
            postJournal.visible(true);
            break;

        case LedgerJournalType::VendPromissoryNoteRedraw :
            checkJournal.visible(true);
            postJournal.visible(true);
            break;

        case LedgerJournalType::VendPromissoryNoteSettle :
            checkJournal.visible(true);
            postJournal.visible(true);
            break;

        case LedgerJournalType::BankChequeReversal :
        case LedgerJournalType::BankDepositPaymCancel :
            checkJournal.visible(true);
            postJournal.visible(true);
            break;

        default :
            checkJournal.visible(true);
            postJournal.visible(true);
    }

    // <GCN>
    if (LedgerParameters::isJournalApproval_CN() == NoYes::No
            || journalFormTable.journalTypeId() != LedgerJournalType::Daily)
    {
        ledgerJournalTable_ApprovalStatus_CN.visible(false);
    }
    // </GCN>

    if (ledgerJournalTable.reportedAsReady())
    {
        reportAsReady.enabled(false);
        approve.enabled(!ledgerJournalTable.approved() || ledgerJournalTable.rejected());
        reject.enabled(!ledgerJournalTable.rejected());
    }
    else
    {
        reportAsReady.enabled(true);
        approve.enabled(false);
        reject.enabled(false);
    }

    if (ledgerJournalTable.ledgerJournalName().ApproveActive)
    {
        if (!UserInfoHelp::userInUserGroup(curUserId(), approveGroupId))
        {
            approve.enabled(false);
            reject.enabled(false);
        }
    }

    //call line enableButtonsActive method
    element.enableButtonsActive_Line();
}

/// <summary>
/// Enables and disables various buttons.
/// </summary>
public void enableButtonsActive_Line()
{
    boolean enabled;
    boolean changeBLWICodeEnabled;
    boolean isUIElementWorkFlowApprovalEnabled;
    boolean aprovalEnabled;

    enabled = journalFormTrans.isJournalEnabled();
    changeBLWICodeEnabled = enabled && (ledgerJournalTrans.AccountType == LedgerJournalACType::Cust || ledgerJournalTrans.AccountType == LedgerJournalACType::Vend ||
                                            ledgerJournalTrans.OffsetAccountType == LedgerJournalACType::Cust || ledgerJournalTrans.OffsetAccountType == LedgerJournalACType::Vend);

    if (journalFormTrans.parmIsActiveWFApprovalDocument())
    {
        isUIElementWorkFlowApprovalEnabled = journalFormTrans.parmIsUIElementWFApprovalEnabled();
        enabled = enabled && isUIElementWorkFlowApprovalEnabled;
        changeBLWICodeEnabled = changeBLWICodeEnabled && isUIElementWorkFlowApprovalEnabled;
    }
    else
    {
        aprovalEnabled = !ledgerJournalEngine.approved() || !ledgerJournalEngine.approveActive();
        enabled = enabled && aprovalEnabled;
        changeBLWICodeEnabled = changeBLWICodeEnabled && aprovalEnabled;
    }

    fixedAssets.enabled(enabled);
    lineStripNew.enabled(enabled);
    lineStripDelete.enabled(enabled);
}

void enableDepositSlipButton()
{
    // We do not allow creating a deposit slip after the journal has been posted and it has been reconciled.
    if (ledgerJournalTrans.ledgerJournalTable().Posted)
    {
        if (ledgerJournalTrans.OffsetAccountType == LedgerJournalACType::Bank)
        {
            depositSlipButton.enabled(!BankAccountTrans::findIsReconciled(ledgerJournalTrans.Voucher,
                ledgerJournalTrans.TransDate,
                ledgerJournalTrans.CurrencyCode,
                ledgerJournalTrans.parmOffsetAccount(),
                - ledgerJournalTrans.amount()));
        }
        if (ledgerJournalTrans.AccountType == LedgerJournalACType::Bank)
        {
            depositSlipButton.enabled(!BankAccountTrans::findIsReconciled(ledgerJournalTrans.Voucher,
                ledgerJournalTrans.TransDate,
                ledgerJournalTrans.CurrencyCode,
                ledgerJournalTrans.parmAccount(),
                ledgerJournalTrans.amount()));
        }
    }
}

//
// This method enables or disables the depreciation start date control.
//

void enableDepreciationStartDate()
{
    AssetBook assetBook;

    assetBook = AssetBook::find(ledgerJournalTrans.getAssetId(), ledgerJournalTrans_Asset.BookId);

    if (assetBook &&
        (ledgerJournalTrans_Asset.TransType == AssetTransTypeJournal::Acquisition || ledgerJournalTrans_Asset.TransType == AssetTransTypeJournal::AcquisitionAdj) &&
        !assetBook.depreciationStarted() &&
        assetBook.DepreciationConvention == AssetDepreciationConvention::None)
    {
        depreciationStartDate.enabled(true);
    }
    else
    {
        depreciationStartDate.enabled(false);
    }
}

void enableFieldsActive()
{
    if (ledgerJournalTable.FixedOffsetAccount   == NoYes::Yes   ||
        ledgerJournalTable.JournalType          == LedgerJournalType::PurchaseLedger)
    {
        ledgerJournalTable_ds.object(fieldNum(LedgerJournalTable, OffsetAccountType)).allowEdit(false);
        /* <GCN>
        ledgerJournalTable_ds.object(fieldNum(LedgerJournalTable, OffsetLedgerDimension)).allowEdit(false);
        </GCN> */
        ledgerJournalTable_ds.object(fieldNum(LedgerJournalTable, OffsetLedgerDimension)).allowEdit(ledgerJournalTable.OffsetAccountType == LedgerJournalACType::Ledger);
    }
    else
    {
        ledgerJournalTable_ds.object(fieldNum(LedgerJournalTable, OffsetAccountType)).allowEdit(true);
        ledgerJournalTable_ds.object(fieldNum(LedgerJournalTable, OffsetLedgerDimension)).allowEdit(true);
    }

    ledgerJournalTable_ds.object(fieldNum(LedgerJournalTable, CustVendNegInstProtestProcess)).allowEdit(ledgerJournalTable.ProtestSettledBill);
    ledgerJournalTable_ds.object(fieldNum(LedgerJournalTable, BankRemittanceType)).allowEdit(!ledgerJournalTable.ProtestSettledBill);

    if (ledgerJournalTable.JournalType == LedgerJournalType::CustPaymRemittance ||
        ledgerJournalTable.JournalType == LedgerJournalType::VendPaymRemittance)
    {
        groupPromissorynote_BankAccountId.allowEdit(!ledgerJournalTable.existVoucher());
    }
    else if (ledgerJournalTable.JournalType == LedgerJournalType::Allocation)
    {
        this.enableFieldsForAllocation();
    }
    else if (ledgerJournalTable.JournalType == LedgerJournalType::Elimination)
    {
        this.enableFieldsForElimination();
    }

    if (journalFormTable.journalTypeId() == LedgerJournalType::Daily)
    {
        ledgerJournalTable_ds.object(fieldNum(LedgerJournalTable, ReverseDate)).allowEdit(ledgerJournalTable.ReverseEntry ? true : false);
    }

    if (journalFormTable.journalTypeId() == LedgerJournalType::BankChequeReversal ||
        journalFormTable.journalTypeId() == LedgerJournalType::BankDepositPaymCancel)
    {
        this.enableFieldsForBank();
    }

    //Call line enableFieldActive
    element.enableFieldsActive_Line();
}

/// <summary>
/// Sets the editable property of various datasource fields for the form.
/// </summary>
public void enableFieldsActive_Line()
{
    boolean     allow;

    ledgerJournalTrans_ds.object(fieldNum(LedgerJournalTrans, ReverseDate)).allowEdit(ledgerJournalTrans.ReverseEntry ? true : false);

    allow = ledgerJournalTable.ledgerJournalName().NewVoucher == NewVoucher::Manually &&
            !ledgerJournalTable.VoucherAllocatedAtPosting;
    ledgerJournalTrans_ds.object(fieldNum(LedgerJournalTrans, Voucher_CN)).enabled(allow);
    ledgerJournalTrans_ds.object(fieldNum(LedgerJournalTrans, Voucher)).enabled(false);
}

/// <summary>
/// Disables most fields when the journal is an allocation journal.
/// </summary>
/// <remarks>
/// An allocation journal does not allow entry of ledgers.
/// </remarks>
void enableFieldsForAllocation()
{
    // the fields in the overview grid and on general tab
    ledgerJournalTable_ds.object(fieldNum(LedgerJournalTable, JournalName)).allowEdit(false);
    ledgerJournalTable_ds.object(fieldNum(LedgerJournalTable, JournalNum)).allowEdit(false);
    ledgerJournalTable_ds.object(fieldNum(LedgerJournalTable, Posted)).allowEdit(false);

    // additional fields on the general tab
    ledgerJournalTable_ds.object(fieldNum(LedgerJournalTable, DetailSummaryPosting)).allowEdit(false);
    ledgerJournalTable_ds.object(fieldNum(LedgerJournalTable, LinesLimitBeforeDistribution)).allowEdit(false);

    // fields on Setup tab are display only
    ledgerJournalTable_ds.object(fieldNum(LedgerJournalTable, OffsetAccountType)).allowEdit(false);
    ledgerJournalTable_ds.object(fieldNum(LedgerJournalTable, OffsetLedgerDimension)).allowEdit(false);
    ledgerJournalTable_ds.object(fieldNum(LedgerJournalTable, DocumentNum)).allowEdit(false);
    ledgerJournalTable_ds.object(fieldNum(LedgerJournalTable, VoucherAllocatedAtPosting)).allowEdit(false);
    ledgerJournalTable_ds.object(fieldNum(LedgerJournalTable, CurrencyCode)).allowEdit(false);
    ledgerJournalTable_ds.object(fieldNum(LedgerJournalTable, FixedExchRate)).allowEdit(false);
    ledgerJournalTable_ds.object(fieldNum(LedgerJournalTable, euroTriangulation)).allowEdit(false);
    editExchRate.enabled(false);
    editExchRateSecond.enabled(false);
    ledgerJournalTable_ds.object(fieldNum(LedgerJournalTable, LedgerJournalInclTax)).allowEdit(false);
    ledgerJournalTable_ds.object(fieldNum(LedgerJournalTable, BankAccountId)).allowEdit(false);
}

/// <summary>
/// Sets the field enablement for the bank journal types.
/// </summary>
void enableFieldsForBank()
{
    // Disable the creation of new journals
    ledgerJournalTable_ds.allowCreate(false);

    // Disable these fields in the overview grid and on general tab
    ledgerJournalTable_ds.object(fieldNum(LedgerJournalTable, JournalName)).allowEdit(false);
    ledgerJournalTable_ds.object(fieldNum(LedgerJournalTable, JournalNum)).allowEdit(false);
    ledgerJournalTable_ds.object(fieldNum(LedgerJournalTable, Posted)).allowEdit(false);
    ledgerJournalTable_ds.object(fieldNum(LedgerJournalTable, Log)).allowEdit(false);

    // Disable this additional field on the general tab
    ledgerJournalTable_ds.object(fieldNum(LedgerJournalTable, LinesLimitBeforeDistribution)).allowEdit(false);

    // Disable all fields on the Setup tab
    ledgerJournalTable_ds.object(fieldNum(LedgerJournalTable, OffsetAccountType)).allowEdit(false);
    ledgerJournalTable_ds.object(fieldNum(LedgerJournalTable, OffsetLedgerDimension)).allowEdit(false);
    ledgerJournalTable_ds.object(fieldNum(LedgerJournalTable, DocumentNum)).allowEdit(false);
    ledgerJournalTable_ds.object(fieldNum(LedgerJournalTable, VoucherAllocatedAtPosting)).allowEdit(false);
    ledgerJournalTable_ds.object(fieldNum(LedgerJournalTable, CurrencyCode)).allowEdit(false);
    ledgerJournalTable_ds.object(fieldNum(LedgerJournalTable, FixedExchRate)).allowEdit(false);
    ledgerJournalTable_ds.object(fieldNum(LedgerJournalTable, euroTriangulation)).allowEdit(false);
    editExchRate.enabled(false);
    editExchRateSecond.enabled(false);
    ledgerJournalTable_ds.object(fieldNum(LedgerJournalTable, LedgerJournalInclTax)).allowEdit(false);
    ledgerJournalTable_ds.object(fieldNum(LedgerJournalTable, BankAccountId)).allowEdit(false);

    // Disable all fields on the Dimensions tab
    tabFinancialDimensions.enabled(false);
}

//
// This method disables most fields when the journal is type Elimination.
//
void enableFieldsForElimination()
{
    // cannot create new journal entries
    if (CompanyInfo::isEliminationCompany() == NoYes::No)
    {
        ledgerJournalTable_ds.allowCreate(false);
    }

    // the fields in the overview grid and on general tab
    ledgerJournalTable_ds.object(fieldNum(LedgerJournalTable, JournalNum)).allowEdit(false);
    ledgerJournalTable_ds.object(fieldNum(LedgerJournalTable, Posted)).allowEdit(false);

    if (ledgerJournalTable.Posted == NoYes::No)
    {
        // this is the Description
        ledgerJournalTable_ds.object(fieldNum(LedgerJournalTable,Name)).allowEdit(true);
    }
    else
    {
        ledgerJournalTable_ds.object(fieldNum(LedgerJournalTable, Name)).allowEdit(false);
    }

    // additional fields on the general tab
    ledgerJournalTable_ds.object(fieldNum(LedgerJournalTable, DetailSummaryPosting)).allowEdit(false);
    ledgerJournalTable_ds.object(fieldNum(LedgerJournalTable, LinesLimitBeforeDistribution)).allowEdit(false);

    // fields on Setup tab are display only
    ledgerJournalTable_ds.object(fieldNum(LedgerJournalTable, OffsetAccountType)).allowEdit(false);
    ledgerJournalTable_ds.object(fieldNum(LedgerJournalTable, OffsetLedgerDimension)).allowEdit(false);
    ledgerJournalTable_ds.object(fieldNum(LedgerJournalTable, DocumentNum)).allowEdit(false);
    ledgerJournalTable_ds.object(fieldNum(LedgerJournalTable, VoucherAllocatedAtPosting)).allowEdit(false);
    ledgerJournalTable_ds.object(fieldNum(LedgerJournalTable, CurrencyCode)).allowEdit(false);
    ledgerJournalTable_ds.object(fieldNum(LedgerJournalTable, FixedExchRate)).allowEdit(false);
    ledgerJournalTable_ds.object(fieldNum(LedgerJournalTable, euroTriangulation)).allowEdit(false);
    editExchRate.enabled(false);
    editExchRateSecond.enabled(false);
    ledgerJournalTable_ds.object(fieldNum(LedgerJournalTable, LedgerJournalInclTax)).allowEdit(false);
    ledgerJournalTable_ds.object(fieldNum(LedgerJournalTable, BankAccountId)).allowEdit(false);

    //fields on Blocking tab- take defaults
    // the Dimensions Tab
    ledgerJournalTable_ds.object(fieldNum(LedgerJournalTable, DefaultDimension)).allowEdit(false);
    // the History Tab
    ledgerJournalTable_ds.object(fieldNum(LedgerJournalTable,ReportedAsReadyBy)).allowEdit(false);
    ledgerJournalTable_ds.object(fieldNum(LedgerJournalTable,Approver)).allowEdit(false);
    ledgerJournalTable_ds.object(fieldNum(LedgerJournalTable,RejectedBy)).allowEdit(false);
    ledgerJournalTable_ds.object(fieldNum(LedgerJournalTable,OriginalCompany)).allowEdit(false);
    ledgerJournalTable_ds.object(fieldNum(LedgerJournalTable,OriginalJournalNum)).allowEdit(false);
    ledgerJournalTable_ds.object(fieldNum(LedgerJournalTable,Posted)).allowEdit(false);
    ledgerJournalTable_ds.object(fieldNum(LedgerJournalTable,PostedDateTime)).allowEdit(false);
    ledgerJournalTable_ds.object(fieldNum(LedgerJournalTable,Log)).allowEdit(false);
}

/// <summary>
/// Specifies whether the project sales currency code can be edited.
/// </summary>
void enableProjSalesCurrencyId()
{
    ProjId  projId;

    projId = ledgerJournalTrans.getProjectId();

    if (projId)
    {
        if (ProjInvoiceTable::isSalesCurrencyLocked(ProjTable::find(projId).ProjInvoiceProjId))
        {
            ledgerJournalTrans_Project_ds.object(fieldNum(LedgerJournalTrans_Project, SalesCurrencyId)).allowEdit(false);
            ledgerJournalTrans_Project_ds.object(fieldNum(LedgerJournalTrans_Project, SalesCurrencyId)).skip(true);
        }
        else
        {
            ledgerJournalTrans_Project_ds.object(fieldNum(LedgerJournalTrans_Project, SalesCurrencyId)).allowEdit(true);
            ledgerJournalTrans_Project_ds.object(fieldNum(LedgerJournalTrans_Project, SalesCurrencyId)).skip(false);
        }
    }
}

display LogTxt errorLog()
{
    return journalFormTrans.parmCheckPostResults().errorLog(ledgerJournalTrans.Voucher);
}

public Tax getShowTax()
{
    return ledgerJournalEngine.getShowTax(ledgerJournalTrans);
}

public void init()
{
    container           lastValue;
    boolean             allow;

    journalFormTable = new LedgerJournalFormTable_CN(element);
    journalFormTrans = new LedgerJournalFormTrans_CN(element);

    enableShowDRsCRs = LedgerShowDebCRedController_CN::isShowDRsCRsFeatureEnabled();

    if (DimensionFocusBalanceUpdateByLedgerFlight::instance().isEnabled())
    {
        MainAccountEventArgs args = MainAccountEventArgs::construct();
        args.parmBalanceLedgerRecId(Ledger::current());

        MainAccount::updateBalancesWithArgs(args);
    }
    else
    {
        MainAccount::updateBalances();
    }

    super();

    //form splitter

    //*****Initialize journal header related****

    //unpack
    lastValue = classfactory.lastValueGet(curext(), curUserId(), UtilElementType::Form, formStr(LedgerJournalVoucher_CN));
    if (lastValue)
    {
        this.unpack(lastValue);
    }
    journalNameForCreation.text(defaultJournalName);

    //Initialize dimAccountContoller - Header
    OffsetAccount.parmIsDefaultAccount(true);
    //Financial dimension tab
    DimensionEntryControl.parmValidateBlockedForManualEntry(true);

    //JournalFormTable initialization
    journalFormTable.parmCtrlAllOpenPosted(allOpenPostedField);
    journalFormTable.parmCtrlShowUserCreatedOnly(showUserCreatedOnly);
    journalFormTable.parmCtrlJournalIdTab(journalNumHeaderTab);
    journalFormTable.parmCtrlJournalIdGrid(ledgerJournalTable_JournalNum1);
    journalFormTable.parmCtrlOverview(journalTableGrid);//used to set first focus once the form is opened.
    journalFormTable.parmCtrlCheckJournal(checkJournal);
    journalFormTable.parmCtrlPostJournalMenu(postJournal);
    journalFormTable.parmCtrlMarkUserBlock(isUserPrivate);//is user private option on header tab.
    journalFormTable.parmCtrlMarkInUseBlock(markInUseBlock);//Mark in use.
    journalFormTable.parmCtrlSystemBlocked(systemBlocked);//system blocked
    journalFormTable.parmCtrlBlockImage(blockImage);//Journal header grid view block image. N/A here
    journalFormTable.parmCtrlApprovalMenu(course);
    journalFormTable.parmCtrlJournalName(journalNameForCreation);
    journalFormTrans.parmCtrlErrorExists(ErrorExist);

    journalFormTable.formInitPost();

    //*****Initialize journal Line related****

    //Initialize dimAccountContoller - Line

    LedgerJournalTrans_AccountNum.parmDimensionAutocompleteFilter(new DimensionAutocompleteVendDefaultFilter(ledgerJournalTrans_ds, fieldStr(LedgerJournalTrans, OffsetLedgerDimension), fieldStr(LedgerJournalTrans, OffsetAccountType)));
    LedgerJournalTrans_AccountNum1.parmDimensionAutocompleteFilter(new DimensionAutocompleteVendDefaultFilter(ledgerJournalTrans_ds, fieldStr(LedgerJournalTrans, OffsetLedgerDimension), fieldStr(LedgerJournalTrans, OffsetAccountType)));
    Group4_AccountNum.parmDimensionAutocompleteFilter(new DimensionAutocompleteVendDefaultFilter(ledgerJournalTrans_ds, fieldStr(LedgerJournalTrans, OffsetLedgerDimension), fieldStr(LedgerJournalTrans, OffsetAccountType)));
    GridOffsetAccount.parmDimensionAutocompleteFilter(new DimensionAutocompleteVendDefaultFilter(ledgerJournalTrans_ds, fieldStr(LedgerJournalTrans, LedgerDimension), fieldStr(LedgerJournalTrans, AccountType)));
    LedgerJournalTrans_OffsetAccount1.parmDimensionAutocompleteFilter(new DimensionAutocompleteVendDefaultFilter(ledgerJournalTrans_ds, fieldStr(LedgerJournalTrans, LedgerDimension), fieldStr(LedgerJournalTrans, AccountType)));
    Group4_OffsetAccount.parmDimensionAutocompleteFilter(new DimensionAutocompleteVendDefaultFilter(ledgerJournalTrans_ds, fieldStr(LedgerJournalTrans, LedgerDimension), fieldStr(LedgerJournalTrans, AccountType)));

    LedgerJournalTrans_AccountNum.parmPostingType(LedgerPostingType::LedgerJournal);
    LedgerJournalTrans_AccountNum1.parmPostingType(LedgerPostingType::LedgerJournal);
    Group4_AccountNum.parmPostingType(LedgerPostingType::LedgerJournal);
    GridOffsetAccount.parmPostingType(LedgerPostingType::LedgerJournal);
    LedgerJournalTrans_OffsetAccount1.parmPostingType(LedgerPostingType::LedgerJournal);
    Group4_OffsetAccount.parmPostingType(LedgerPostingType::LedgerJournal);
    CustPaymJournalFee_CustAccount.parmPostingType(LedgerPostingType::LedgerJournal);

    LedgerJournalTrans_AccountNum.parmValidateBlockedForManualEntry(true);
    LedgerJournalTrans_AccountNum1.parmValidateBlockedForManualEntry(true);
    Group4_AccountNum.parmValidateBlockedForManualEntry(true);
    GridOffsetAccount.parmValidateBlockedForManualEntry(true);
    LedgerJournalTrans_OffsetAccount1.parmValidateBlockedForManualEntry(true);
    Group4_OffsetAccount.parmValidateBlockedForManualEntry(true);
    CustPaymJournalFee_CustAccount.parmValidateBlockedForManualEntry(true);

    // Construct the entity manager to help manage entity-related tasks like in-memory rollbacks for aborted transactions.
    ledgerJournalTransEntityManager = LedgerJournalTransEntityManager::construct(ledgerJournalTrans_DS, ledgerJournalTrans_Asset_DS, ledgerJournalTrans_Project_DS);

    //journalFormTrans initialization
    journalFormTrans.parmCtrlVoucherGrid(ledgerJournalTrans_Voucher);
    journalFormTrans.parmCtrlVoucherTab(ledgerJournalTrans_Voucher1);
    journalFormTrans.parmCtrlTransDateGrid(ledgerJournalTrans_TransDate);
    journalFormTrans.parmCtrlTransDateTab(ledgerJournalTrans_TransDate1);
    journalFormTrans.parmCtrlExchRate(fieldExchRate);
    journalFormTrans.parmCtrlExchRateSecond(fieldExchRateSecond);
    journalFormTrans.parmCtrlBankDepositVoucherGrid(bankDepositVoucherGrid);
    journalFormTrans.parmCtrlBankDepositVoucherTab(bankDepositVoucherTab);
    journalFormTrans.parmCtrlSettlementButton(settlementButton);
    journalFormTrans.parmCtrlDepositSlipButton(depositSlipButton);
    journalFormTrans.parmCtrlVoucherTransButton(buttonVoucherTrans);
    journalFormTrans.parmCtrlDeleteTransactionsButton(buttonDeleteTransactions);
    journalFormTrans.parmCtrlAccrualButton(ledgerJournalTransAccrual);
    journalFormTrans.parmCtrlBalanceControButton(buttonBalanceControl);
    journalFormTrans.parmCtrlGetTransactionsButton(buttonGetTransactions);
    journalFormTrans.parmCtrlSplitPostingButton(buttonSplitPosting);
    journalFormTrans.parmCtrlFurtherPostingButton(buttonFurtherPosting);
    journalFormTrans.parmCtrlCopyPeriodicButton(buttonCopyPeriodic);
    journalFormTrans.parmCtrlJournalTextsButton(ledgerJournalTransTxt);
    journalFormTrans.parmCtrlFunctionMenuButton(functions);
    journalFormTrans.parmCtrlSalesTaxMenu(taxTransSource);
    journalFormTrans.parmCtrlDepositSlipButton(depositSlipButton);
    journalFormTrans.parmCtrlPeriodicJournalMenuButton(periodicJournal);
    journalFormTrans.parmCtrlSelectVoucherTemplateButton(selectVoucherTemplate);
    journalFormTrans.parmCtrlSaveVoucherTemplateButton(saveVoucherTemplate);
    journalFormTrans.parmCtrlAccountDimensionButton(accountDimension);
    journalFormTrans.parmCtrlOffsetAccountDimensionButton(offsetAccountDimension);
    journalFormTrans.parmCtrlPaymFeeAccountDimensionButton(paymentFeeAccountDimension);
    journalFormTrans.parmCalculatedTaxAmount(taxAmountJournal);
    journalFormTrans.parmActualTaxAmount(correctedTaxAmount);

    journalFormTrans.parmCtrlAddbutton(lineStripNew);
    journalFormTrans.parmCtrlDeleteButton(lineStripDelete);

    journalFormTrans.formInitPost();

    this.initLedger();

    TaxVATNumTable::enableLookupVATNum(vatNumJournal);

    cashDiscChanged = false;
    imageListAppl   = new ImageListAppl_LedgerJournal();
    errorExist.imageList(imageListAppl.imageList());

    if (! TaxParameters::find().CashDiscOnInvoice)
        ledgerJournalTrans_ds.object(fieldNum(LedgerJournalTrans, CashDiscPercent)).allowEdit(false);
    else
         ledgerJournalTrans_ds.object(fieldNum(LedgerJournalTrans, DateCashDisc)).visible(false);

    // Disable the company accounts field on the general tab if Intercompany transactions are disabled
    if (!isConfigurationkeyEnabled(configurationKeyNum(LedgerAdvIntercompanyAccounting)))
    {
        ledgerJournalTrans_Company.allowEdit(false);
    }

    allow = ledgerJournalTable.ledgerJournalName().NewVoucher == NewVoucher::Manually &&
            !ledgerJournalTable.VoucherAllocatedAtPosting;
    ledgerJournalTrans_ds.object(fieldNum(LedgerJournalTrans, Voucher_CN)).enabled(allow);
    ledgerJournalTrans_ds.object(fieldNum(LedgerJournalTrans, Voucher)).enabled(false);

    //if the CN journal approval is not enabled in GL parameters, hide the field.
    if (LedgerParameters::isJournalApproval_CN() == NoYes::No)
    {
        ledgerJournalTable_ApprovalStatus_CN.visible(false);
    }

    this.enableBankLC();

    hcmWorkerLookupActiveCurrentCompany = HcmWorkerLookup::newOnlyActiveWorkersWithinCompany();
    keepSalesPriceOnWrite = false;
}

/// <summary>
/// Initializes form controls based on the journal type.
/// </summary>
/// <param name="_journalTypeId">
/// The journal type ID
/// </param>
protected void initControls(Integer _journalTypeId)
{
    // hide the Reversing Entries fields
    groupReversingEntry.visible(false);

    if (LedgerJournalForcedJournalSplitFeature::isEnabled())
    {
        LedgerJournalTable_LinesLimitBeforeDistribution.visible(false);
    }

    switch (_journalTypeId)
    {
        case LedgerJournalType::CustBillOfExchangeProtest :
            tabBillOfExchange.visible(true);
            groupProtestNegInst.visible(true);
            groupBillOfExchange_BankRemittanceType.hideFirstEntry(false);
            break;

        case LedgerJournalType::CustBillOfExchangeDraw,
             LedgerJournalType::CustBillOfExchangeRedraw:
             tabBillOfExchange.visible(true);
            tabBillOfExchange.caption("@SYS69795");
            groupBillOfExchange_BankRemittanceType.visible(false);
            break;

        case LedgerJournalType::CustPaymRemittance,
             LedgerJournalType::CustBillOfExchangeSettle :
             tabBillOfExchange.visible(true);
            tabBillOfExchange.caption("@SYS69795");
            break;

        case LedgerJournalType::VendPromissoryNoteDraw,
             LedgerJournalType::VendPaymRemittance,
             LedgerJournalType::VendPromissoryNoteSettle :
             tabBillOfExchange.visible(true);
            tabBillOfExchange.caption("@SYS71440");
            billOfExchange.visible(false);
            promissoryNote.visible(true);
            break;

        case LedgerJournalType::VendPromissoryNoteRedraw :
            tabBillOfExchange.visible(true);
            tabBillOfExchange.caption("@SYS71440");
            billOfExchange.visible(false);
            promissoryNote.visible(true);
            groupProtestNegInst.visible(true);
            break;

        case LedgerJournalType::Allocation :
            // cannot create new journal entries
            ledgerJournalTable_ds.allowCreate(false);
            break;

        case LedgerJournalType::Daily :
            // show the Reversing Entries fields
            groupReversingEntry.visible(true);
            break;

        case LedgerJournalType::PayrollDisbursement:
            LedgerJournalTable_LinesLimitBeforeDistribution.visible(true);
            break;

        case LedgerJournalType::Assets,
             LedgerJournalType::AssetBudgets:
            LedgerJournalTable_LinesLimitBeforeDistribution.visible(!AssetIgnoreJournalSplittingFlight::instance().isEnabled());
            break;

        default :
        // Default properties of the tabpage
    }
}

public void initLedger()
{
    journalNum          = ledgerJournalTable.JournalNum;

    LedgerJournalTrans_AccountNum.parmJournalName(ledgerJournalTable.JournalName);
    LedgerJournalTrans_AccountNum1.parmJournalName(ledgerJournalTable.JournalName);
    Group4_AccountNum.parmJournalName(ledgerJournalTable.JournalName);

    CustPaymJournalFee_CustAccount.parmJournalName(ledgerJournalTable.JournalName);

    currentOffsetMainAccountId = GridOffsetAccount.getValue(
        DimensionAttribute::getWellKnownDimensionAttribute(DimensionAttributeType::MainAccount));

    // Lock the main account segment if "Fixed offset account" is selected in Journal Names
    if (ledgerJournalTrans.OffsetAccountType == LedgerJournalACType::Ledger)
    {
        GridOffsetAccount.parmLockMainAccountSegment(ledgerJournalTable.FixedOffsetAccount);
    }

    if (ledgerJournalTable.FixedOffsetAccount && ledgerJournalTable.parmOffsetLedgerDimension())
    {
        ledgerJournalTrans_OffsetAccountType1.allowEdit(false);
        /* <GCN>
        ledgerJournalTrans_OffsetAccount1.allowEdit(false);
        </GCN> */
        ledgerJournalTrans_OffsetAccount1.allowEdit(ledgerJournalTrans.OffsetAccountType == LedgerJournalACType::Ledger);
        ledgerJournalTrans_OffsetCompany.allowEdit(false);
    }

    if (ledgerJournalTable.FixedOffsetAccount)
    {
        gridOffsetAccount.allowEdit(ledgerJournalTrans.OffsetAccountType == LedgerJournalACType::Ledger);
        group4_OffsetAccount.allowEdit(ledgerJournalTrans.OffsetAccountType == LedgerJournalACType::Ledger);
    }
    else if (!group4_OffsetAccount.allowEdit())
    {
        group4_OffsetAccount.allowEdit(true);
    }
    else if (!gridOffsetAccount.allowEdit())
    {
        gridOffsetAccount.allowEdit(true);
    }
}

