public boolean modified()
{
    boolean         ret;

    ret = super();

    if (this.valueStr())
    {
        ret = ret && LedgerJournalName::checkExist(this.valueStr());
    }

    if (ret)
    {
        defaultJournalName = this.valueStr();
        journalFormTable.designSelectionChangeJournalName();
    }
    else
    {
        this.text(defaultJournalName);
    }

    return ret;
}

