public void pageActivated()
{
    super();

    // Need to enable\Disable the Manage Address button depending
    // on an account been selected or not.
    if (ledgerJournalTrans.parmAccount() == '')
    {
        remittance_ManageAddress.enabled(false);
        addAddress.enabled(false);
    }
    else
    {
        remittance_ManageAddress.enabled(true);
        addAddress.enabled(true);
    }
}

public void lookup()
{
    switch (ledgerJournalTable.OffsetAccountType)
    {
        case LedgerJournalACType::Bank:
            BankAccountTable::lookupBankAccount(this);
            break;
        case LedgerJournalACType::Cust:
            CustTable::lookupCustomer(this);
            break;
        case LedgerJournalACType::FixedAssets:
            AssetTable::lookupAccountNum(this);
            break;
        case LedgerJournalACType::Project:
            ProjTable::lookupProjId(this, ledgerJournalTable);
            break;
        case LedgerJournalACType::Vend:
            VendTable::lookupVendor(this);
            break;
        default:
            super();
            break;
    }
}

public boolean checkUseCustomLookup(int _accountTypeEnumValue, int _secondaryAccountTypeEnumValue)
{
    boolean returnValue;

    LedgerJournalACType accountType = any2Enum(_accountTypeEnumValue);

    switch (accountType)
    {
        case LedgerJournalACType::Bank:
        case LedgerJournalACType::Cust:
        case LedgerJournalACType::FixedAssets:
        case LedgerJournalACType::Project:
        case LedgerJournalACType::Vend:
            returnValue = true;
            break;
        default:
            returnValue = false;
            break;
    }

    return returnValue;
}

