/// <summary>
/// This method switches the synchronus GL transfer option if present to Asynchronous as we have depricated the Synchronous
/// option.
/// </summary>
internal void updateGLTransferModeToAsync()
{
    SubledgerJournalTransferRule updateSubledgerJournalTransferRule;

    select firstonly updateSubledgerJournalTransferRule
        where updateSubledgerJournalTransferRule.TransferMode == SubledgerJournalTransferMode::Synchronous;

    if (updateSubledgerJournalTransferRule)
    {
        updateSubledgerJournalTransferRule = null;

        ttsbegin;
        update_recordset updateSubledgerJournalTransferRule
            setting TransferMode = SubledgerJournalTransferMode::AsynchronousBatch
        where updateSubledgerJournalTransferRule.TransferMode == SubledgerJournalTransferMode::Synchronous;
        ttscommit;
    }
}

