/// <summary>
/// Enables or disables the <c>IsSummarizationEnabled</c> field of the <c>SubledgerJournalTransferRule</c> table.
/// </summary>
/// <remarks>
/// The <c>IsSummarizationEnabled</c> field is disabled when the <c>TransferMode</c> field is Synchronous.
/// </remarks>
public void enableIsSummarizationEnabled()
{
    if (subledgerJournalTransferRule.TransferMode==SubledgerJournalTransferMode::Synchronous ||
        subledgerJournalTransferRule.TransferMode==SubledgerJournalTransferMode::AsynchronousBatch)
    {
        subledgerJournalTransferRule_ds.object(fieldNum(SubledgerJournalTransferRule, IsSummarizationEnabled)).enabled(false);
    }
    else
    {
        subledgerJournalTransferRule_ds.object(fieldNum(SubledgerJournalTransferRule, IsSummarizationEnabled)).enabled(true);
    }
}

public void enableNotificationGroup()
{
    if (subledgerJournalTransferRule.TransferMode==SubledgerJournalTransferMode::Synchronous)
    {
        subledgerJournalTransferRule_ds.object(fieldNum(SubledgerJournalTransferRule, NotificationUserGroupId)).enabled(false);
    }
    else
    {
        subledgerJournalTransferRule_ds.object(fieldNum(SubledgerJournalTransferRule, NotificationUserGroupId)).enabled(true);
    }
}

internal void enableOrDisableKeepDetails()
{
    if (isAdvancedAwarenessOptsParmEnabled)
    {
        // This option can only be marked if the main account is setup with a Main account type of Balance sheet (Asset,Liability,Equity) accounts.
        if (mainAccount && mainAccount.isBalanceSheetAccount())
        {
            ledgerTransSettlementAccount_ds.object(fieldNum(LedgerTransSettlementAccount, KeepDetails)).enabled(true);
        }
        else
        {
            ledgerTransSettlementAccount_ds.object(fieldNum(LedgerTransSettlementAccount, KeepDetails)).enabled(false);
        }
    }
}

internal void defaultTypicalBalance(DebCredProposal _debitCreditProposal)
{
    if (isAdvancedAwarenessOptsParmEnabled)
    {
        switch (_debitCreditProposal)
        {
            case DebCredProposal::Credit:
                ledgerTransSettlementAccount_ds.object(fieldNum(LedgerTransSettlementAccount, TypicalBalance)).setValue(LedgerSettlementsAccountTypicalBalance::Credit);
                break;
            case DebCredProposal::None:
            case DebCredProposal::Debit:
                ledgerTransSettlementAccount_ds.object(fieldNum(LedgerTransSettlementAccount, TypicalBalance)).setValue(LedgerSettlementsAccountTypicalBalance::Debit);
                break;
        }
    }
    else
    {
        ledgerTransSettlementAccount_ds.object(fieldNum(LedgerTransSettlementAccount, TypicalBalance)).setValue(LedgerSettlementsAccountTypicalBalance::Debit);
    }
}

