EnumName getSourceDocumentType(boolean _set, SubledgerJournalTransferRule _subledgerJournalTransferRule, EnumName _documentTypeName)
{
    EnumName documentTypeName;

    if (_set)
    {
        select firstonly ClassId from sourceDocumentTmpTypeInformation
            where sourceDocumentTmpTypeInformation.EnumTranslatedName == _documentTypeName;

        if (sourceDocumentTmpTypeInformation)
        {
            _subledgerJournalTransferRule.SourceDocumentType = sourceDocumentTmpTypeInformation.ClassId;

            //not specified, company level
            if (sourceDocumentTmpTypeInformation.ClassId == 0)
            {
                ruleType.selection(SubledgerJournalTransferRuleType::LegalEntity);
            }
            else
            {
                ruleType.selection(SubledgerJournalTransferRuleType::SourceDocumentType);
            }
        }
        else
        {
            subledgerJournalTransferRule.SourceDocumentType = #InvalidSourceDocumentType;
        }
        documentTypeName = _documentTypeName;
    }
    else
    {
        if (_subledgerJournalTransferRule.SourceDocumentType != 0)
        {
            select firstonly EnumTranslatedName from sourceDocumentTmpTypeInformation
                where sourceDocumentTmpTypeInformation.ClassId == _subledgerJournalTransferRule.SourceDocumentType;

            if (sourceDocumentTmpTypeInformation)
            {
                documentTypeName =  sourceDocumentTmpTypeInformation.EnumTranslatedName;
            }
        }
        else
        {
            documentTypeName = "@SYS324817";
        }
    }

    return documentTypeName;
}

