    // </GCN>

    // <GJP>
    financialReportStyle_JP = SysCountryRegionCode::isLegalEntityInCountryRegion([#isoJP]);
    // </GJP>

    if (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoMX]))
