    }
    // </GSA>
    element.loadSourceDocumentTypeInformation();

    if (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoBE]))
    {
        localSubledgerJournalTransferRule = SubledgerJournalTransferRule::findBestMatchRule(classNum(CustInvoiceSourceDoc), Ledger::current());
        if (localSubledgerJournalTransferRule.SourceDocumentType ==  classNum(CustInvoiceSourceDoc) && localSubledgerJournalTransferRule.IsSummarizationEnabled)
        {
            isValid = false;
        }
        else
        {
            localSubledgerJournalTransferRule = SubledgerJournalTransferRule::findBestMatchRule(classNum(VendorInvoiceSourceDoc), Ledger::current());
            if (localSubledgerJournalTransferRule.SourceDocumentType ==  classNum(VendorInvoiceSourceDoc) && localSubledgerJournalTransferRule.IsSummarizationEnabled)
            {
                isValid = false;
            }
        }

        if (!isValid)
        {
            warning(strFmt("@GLS222827", classId2Name(localSubledgerJournalTransferRule.SourceDocumentType)));
        }
    }

    // <GTH>
    if (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoTH])
        && isConfigurationkeyEnabled(configurationKeyNum(TaxThailandGovCertification)))
    {
        this.updateDesignForAverageVAT();
    }
    // </GTH>

    // Disable the dimension delimiter if combinations already exist
    DimensionProviderBase dimensionProvider = DimensionProviderFactory::getDimensionProvider();

    if (!dimensionProvider.isChartOfAccountsDelimiterEditable())
    {
        DimensionsDelimiter.enabled(false);
    }

    // <GTE>
    if (isGTEEnabled)
    {
        Exim_IN.visible(false);
        Exim_INCopy1_SalesTax_IN.visible(false);
        Exim_INCopy1_Excise_IN.visible(false);
        Exim_INCopy1_ServiceTax_IN.visible(false);
    }
    // </GTE>

    TaxCountryReportingTabPage.visible(isGTEEnabled || TaxIntegrationUtils::isMultipleTaxIdEnabledForVATDeclarations());

    //Feature Sales Tax Conversion
    TaxConversionPath.visible(TaxCurrencyConversionFeatureExposure::isVisible());

    taxParameters_ds.object(fieldNum(TaxParameters, CheckSalesTaxTransaction)).allowEdit(TaxParameters::find().CheckIntersection != CheckTaxGroups::None);

    YearEnding_DeleteOpening.label("@GeneralLedger:DeleteOpeningLabel");
    YearEnding_DeleteOpening.helpText("@GeneralLedger:DeleteOpeningHelpText");
    YearEnding_OpeningVoucher.visible(false);

    // Show some tax parameters only if the tax debug mode is enabled (the URL contains the specific flag).
    try
    {
        Microsoft.Dynamics.Client.ServerForm.Contexts.SessionContext currentSession = Microsoft.Dynamics.Client.ServerForm.Contexts.SessionContext::Current;
        if (currentSession && currentSession.RequestUrl && strContains(currentSession.RequestUrl.ToString(), URLFlagToEnableTaxDebugMode))
        {
            taxParameters_ds.object(fieldNum(TaxParameters, TaxCalculationRetryNumber)).visible(true);
            TaxParameters_TaxCalculationRetryNumber.visible(true);
        }
    }
    catch
    {
        // Ignore any exception and the tax debug mode will not impact any application logic.
    }

    taxParameters_ds.object(fieldNum(TaxParameters, TaxReportCombineVoucherForGainLoss)).visible(TaxReportPerfImprovByVoucherTransCombineFlight::instance().isEnabled());

