    FinTagDelimiters_Delimiter.enabled(FinTagParameters::isDelimiterEditable());

    this.updateDesignForTaxSpecificExchangeRate();
}

/// <summary>
/// Displays the tax specific exchange rate feature related fields.
/// </summary>
private void updateDesignForTaxSpecificExchangeRate()
{
    boolean isTaxSpecificExchangeRateFeatureEnabled =
        Tax::isTaxIntegrationEnabledForTaxService() // Tax specific exchange rate feature is only supported for tax calculation service.
        && FeatureStateProvider::isFeatureEnabled(TaxSpecificExchangeRateFeature::instance())
        && !SysCountryRegionCode::isLegalEntityInCountryRegion([#isoIN, #isoBR]);

    TaxConversionPath.allowEdit(!TaxSpecificExchangeRateHelper::isTaxSpecificExchangeRateEnabled());

    boolean showBankExchangeRateFields = SysCountryRegionCode::isLegalEntityInCountryRegion([#isoCZ, #isoHU, #isoPL])
        || isTaxSpecificExchangeRateFeatureEnabled;

    taxParameters_ds.object(fieldNum(TaxParameters, BankExchRate_W)).visible(showBankExchangeRateFields);
    taxParameters_ds.object(fieldNum(TaxParameters, SalesTaxPayableExchangeRateType)).visible(showBankExchangeRateFields);
    taxParameters_ds.object(fieldNum(TaxParameters, SalesTaxReceivableExchangeRateType)).visible(showBankExchangeRateFields);

    if (FeatureStateProvider::isFeatureEnabled(TaxSpecificExchangeRateFeature::instance())
        && !SysCountryRegionCode::isLegalEntityInCountryRegion([#isoIN, #isoBR]))
    {
        Bank_BankExchRate_W.label("@TaxIntegration:TaxSpecificExchangeRateFeature");
        Bank.caption("@TaxIntegration:ExchangeRateTypes");
    }
}

