private void setAdvancedAwarenessOptionsParameter()
{
    // This parameter cannot be enabled when using the public sector year-end close in General ledger.
    if (isConfigurationkeyEnabled(configurationKeyNum(PublicSector)) && ledgerParameters.LedgerYearEndClosingEnabled_PSN)
    {
        LedgerParameters_EnableAdvAwarenessOption.enabled(false);
        LedgerParameters_EnableAdvAwarenessOption.helpText("@GeneralLedger:LedgerSettlementAdvancedAwarenessOptionPSYEC");
        ledgerParameters_ds.object(fieldNum(LedgerParameters, EnableLedgerSettlementAdvancedAwarenessOptions)).allowEdit(false);
    }
    else if (ledgerParameters.EnableLedgerSettlement != UnknownNoYes::Yes)
    {
        // Disable all advanced parameters if LedgerSettlement is disabled
        LedgerParameters_EnableAdvAwarenessOption.enabled(false);
        ledgerParameters_ds.object(fieldNum(LedgerParameters, EnableLedgerSettlementAdvancedAwarenessOptions)).allowEdit(false);
        ledgerParameters_ds.object(fieldNum(LedgerParameters, EnableLedgerSettlementAdvancedAwarenessOptions)).setValue(NoYes::No);

        LedgerParameters_EnablePASettlement.enabled(false);
        ledgerParameters_ds.object(fieldNum(LedgerParameters, EnableLedgerSettlementProcessAutomation)).allowEdit(false);
        ledgerParameters_ds.object(fieldNum(LedgerParameters, EnableLedgerSettlementProcessAutomation)).setValue(NoYes::No);

        LedgerParameters_EnableRealizedGainLoss.enabled(false);
        ledgerParameters_ds.object(fieldNum(LedgerParameters, EnableLedgerSettlementPostRealizedGainLoss)).allowEdit(false);
        ledgerParameters_ds.object(fieldNum(LedgerParameters, EnableLedgerSettlementPostRealizedGainLoss)).setValue(NoYes::No);
    }
    else
    {
        boolean isAdvancedAwarenessOptionsEnabled = ledgerParameters.EnableLedgerSettlementAdvancedAwarenessOptions == NoYes::Yes;

        LedgerParameters_EnableAdvAwarenessOption.enabled(true);
        ledgerParameters_ds.object(fieldNum(LedgerParameters, EnableLedgerSettlementAdvancedAwarenessOptions)).allowEdit(true);

        // Enable PA settlement parameter is enabled only when Awareness parameter is enabled
        LedgerParameters_EnablePASettlement.enabled(isAdvancedAwarenessOptionsEnabled);
        ledgerParameters_ds.object(fieldNum(LedgerParameters, EnableLedgerSettlementProcessAutomation)).allowEdit(isAdvancedAwarenessOptionsEnabled);

        // Enable Post realized gain/loss for settlement parameter is enabled only when Awareness parameter is enabled
        LedgerParameters_EnableRealizedGainLoss.enabled(isAdvancedAwarenessOptionsEnabled);
        ledgerParameters_ds.object(fieldNum(LedgerParameters, EnableLedgerSettlementPostRealizedGainLoss)).allowEdit(isAdvancedAwarenessOptionsEnabled);

        if (!isAdvancedAwarenessOptionsEnabled)
        {
            ledgerParameters_ds.object(fieldNum(LedgerParameters, EnableLedgerSettlementProcessAutomation)).setValue(NoYes::No);
            ledgerParameters_ds.object(fieldNum(LedgerParameters, EnableLedgerSettlementPostRealizedGainLoss)).setValue(NoYes::No);
        }

        MainAccountTypeComboBox.visible(isAdvancedAwarenessOptionsEnabled);
        MainAccount_ds.object(fieldNum(MainAccount, Type)).visible(isAdvancedAwarenessOptionsEnabled);

        KeepDetailsComboBox.visible(isAdvancedAwarenessOptionsEnabled);
        ledgerTransSettlementAccount_ds.object(fieldNum(LedgerTransSettlementAccount, KeepDetails)).visible(isAdvancedAwarenessOptionsEnabled);
    }

    this.setLedgerSettlementAccountRealizedGainLossColumn();
}

private void setLedgerSettlementAccountRealizedGainLossColumn()
{
    boolean isPostRealizedGainLossParameterEnabled = ledgerParameters.EnableLedgerSettlementPostRealizedGainLoss == NoYes::Yes;
    CalculateRealizedGainsLossesComboBox.visible(isPostRealizedGainLossParameterEnabled);
}

