void numberSeqPreInit()
{
    runExecuteDirect   = false;

    // <GEERU>
    if (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoRU]))
    {
        numberSequenceModules = [NumberSeqModule::Ledger, NumberSeqModule::Budget, NumberSeqModule::Tax, NumberSeqModule::Asset
                             ,NumberSeqModule::RDeferrals, NumberSeqModule::RTSL, NumberSeqModule::RTax25];
    }
    else
    {
        // </GEERU>
        numberSequenceModules = [NumberSeqModule::Ledger, NumberSeqModule::Tax];
        // <GEERU>
    }
    // </GEERU>
    numberSeqApplicationModule = new NumberSeqModuleLedger();
    scope = NumberSeqScopeFactory::createDataAreaScope();
    NumberSeqApplicationModule::createReferencesMulti(numberSequenceModules, scope);

    tmpIdRef.setTmpData(NumberSequenceReference::configurationKeyTableMulti(numberSequenceModules));
}

