public int active()
{
    int ret;

    ret = super();

    element.enablePostingDefinitionParameters();
    element.enableAcccountingRulesParameters();
    element.displayLedgerSettlementsSetup();
    element.setAdvancedAwarenessOptionsParameter();

    // <GEERU>
    if (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoRU]))
    {
        element.updateDesign_W();
    }
    // </GEERU>

    // <GCN>
    if (countryRegion_CN)
    {
        element.updateControls_CN();
    }
    // </GCN>

    return ret;
}

