public boolean validate()
{
    PurchReqLineProcessingError     processingError;
    PurchReqLine                    purchReqLine;
    boolean                         ret;
    RecId                           currentCompanyId;

    ret = super();

    if (!PublicSectorUtils::isBudgetReservationEnabled())
    {
        if (ledgerParameters.IsPreEncumbranceProcessEnabled)
        {
            if (ret)
            {
                Box::info("@SYS314092");
            }
        }
        else
        {
            currentCompanyId = CompanyInfo::findDataArea(curext()).RecId;

            select firstonly RecId, PurchReqLine from processingError
            where processingError.Type == PurchReqLineProcessingErrorType::PreEncumbranceError
        join RecId, BuyingLegalEntity from purchReqLine
            where purchReqLine.RecId == processingError.RecId
               && purchReqLine.BuyingLegalEntity == currentCompanyId;

            if (processingError.RecId != 0)
            {
                Box::stop("@SYS314094");
                ret = false;
            }
        }
    }
    return ret;
}

