public int active()
{
    int ret;

    ret = super();

    FormDataSource fds = this;
    boolean isHideAutoSalesTaxSearchFlightEnabled = RetailHideAutoSalesTaxGroupSearchFlight::instance().isEnabled();

    // Hide MCRAutoTaxSearch when RetailHideAutoSalesTaxGroupSearchFlight flight is enabled.
    fds.object(fieldNum(TaxParameters, MCRAutoTaxSearch)).visible(!isHideAutoSalesTaxSearchFlightEnabled);

    element.setupAllowEditCustVendCashDisc();
    element.switchVatBookEnable();

    //<GIN>
    if (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoIN])
        && ret)
    {
        element.enableIncentiveSchemes_IN();
    }
    //</GIN>

    //<GEEU>
    if (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoHU])
        && ret)
    {
        element.updateDesign_HU();
    }
    //</GEEU>

    //<GMY>
    if (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoMY])
        && ret)
    {
        element.updateDesign_MY();
    }
    //</GMY>

    return ret;
}

