edit EnumName sourceDocumentType(boolean _set, SubledgerJournalTransferRule _subledgerJournalTransferRule, EnumName _documentTypeName)
{
    return element.getSourceDocumentType(_set, _subledgerJournalTransferRule, _documentTypeName);
}

edit SubledgerJournalTransferRuleType ruleType(boolean _set, SubledgerJournalTransferRule _subledgerJournalTransferRule, SubledgerJournalTransferRuleType _ruleType)

{
    SubledgerJournalTransferRuleType transferRuleType;

    if (_set)
    {
        if (_ruleType == SubledgerJournalTransferRuleType::LegalEntity)
        {
            _subledgerJournalTransferRule.SourceDocumentType = 0;
        }
        else
        {
            _subledgerJournalTransferRule.SourceDocumentType = classNum(PurchaseOrderSourceDocumentType);
        }

        transferRuleType = _ruleType;
    }
    else
    {
        if (_subledgerJournalTransferRule.SourceDocumentType == 0)
        {
            transferRuleType = SubledgerJournalTransferRuleType::LegalEntity;
        }
        else
        {
            transferRuleType = SubledgerJournalTransferRuleType::SourceDocumentType;
        }
    }

    return transferRuleType;
}

public boolean validateWrite()
{
    boolean isValid = true;

    isValid = super();

    if (ruleType.selection()== SubledgerJournalTransferRuleType::SourceDocumentType && subledgerJournalTransferRule.SourceDocumentType == -1)
    {
        isValid = checkFailed("@SYS320156");
    }

    if ((subledgerJournalTransferRule.orig().TransferMode != SubledgerJournalTransferMode::Synchronous
        && subledgerJournalTransferRule.TransferMode == SubledgerJournalTransferMode::Synchronous)
        || (subledgerJournalTransferRule.RecId == 0 && subledgerJournalTransferRule.TransferMode == SubledgerJournalTransferMode::Synchronous))
    {
        Box::info("@SourceDocumentation:SourceDoc_BatchTransferRule_WarningMessageForSynchronousTransferOption");

        if (subledgerJournalTransferRule.orig().TransferMode != SubledgerJournalTransferMode::Synchronous)
        {
            subledgerJournalTransferRule.TransferMode = subledgerJournalTransferRule.orig().TransferMode;
        }
        else
        {
            subledgerJournalTransferRule.TransferMode = SubledgerJournalTransferMode::AsynchronousBatch;
        }
    }

    return isValid;
}

public void create(boolean _append = false)
{
    ClassId sourceDocumentClassId;

    super(_append);

    sourceDocumentClassId = #InvalidSourceDocumentType;
    subledgerJournalTransferRule.TransferMode = SubledgerJournalTransferMode::ScheduledBatch;
    subledgerJournalTransferRule.NotificationUserGroupId = '';
    subledgerJournalTransferRule.IsSummarizationEnabled = NoYes::Yes;
    ruleType.selection(SubledgerJournalTransferRuleType::SourceDocumentType);
    subledgerJournalTransferRule.SourceDocumentType = sourceDocumentClassId;
}

public int active()
{
    int ret;

    ret = super();

    element.enableIsSummarizationEnabled();
    element.enableNotificationGroup();
    if (subledgerJournalTransferRule.SourceDocumentType == 0)
    {
        sourceDocumentType.allowEdit(false);
    }
    else
    {
        sourceDocumentType.allowEdit(true);
    }

    return ret;
}

public void modified()
{
    super();

    if (subledgerJournalTransferRule.TransferMode==SubledgerJournalTransferMode::Synchronous ||
        subledgerJournalTransferRule.TransferMode==SubledgerJournalTransferMode::AsynchronousBatch)
    {
        subledgerJournalTransferRule.IsSummarizationEnabled = NoYes::No;
        subledgerJournalTransferRule.NotificationUserGroupId = '';
    }
    element.enableIsSummarizationEnabled();
    element.enableNotificationGroup();
}

