[Hookable(false)]
public boolean validateWrite()
{
    boolean ret;
    FinTagSegmentDelimiter tagDelimiter = FinTagDelimiters_Delimiter.selection();
    str message = strFmt("@FinTag:FinancialTag_Parameters_Message_ConfirmDelimiterSetup", tagDelimiter);

    if (Box::yesNo(message, DialogButton::No) == DialogButton::Yes)
    {
        FinTagDelimiters_Delimiter.enabled(!(FinTag::existsAny()));
        ret = true;
    }
    else
    {
        ret = false;
    }

    return ret;
}

